import React, { useEffect, useState } from "react";
import BrandList from "./BrandList";  // ✅ Tự động lấy trong thư mục Brand/
import BrandForm from "./BrandForm";  // ✅ Tự động lấy trong thư mục Brand/
import { getBrands, deleteBrand } from "../../../../services/brandService";

import "../Brand/BrandStyles.css";

const BrandPage = () => {
    const [brands, setBrands] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [editBrand, setEditBrand] = useState(null);

    useEffect(() => {
        fetchBrands();
    }, []);

    const fetchBrands = async () => {
        setBrands(await getBrands());
    };

    const handleDelete = async (id) => {
        if (window.confirm("Bạn có chắc muốn xóa Brand này?")) {
            await deleteBrand(id);
            fetchBrands();
        }
    };

    return (
        <div className="brand-container">
            <h2>Manage Brands</h2>
            <button className="add-btn" onClick={() => setShowForm(true)}>➕ Add Brand</button>

            <BrandList brands={brands} onEdit={(brand) => {
                setEditBrand(brand);
                setShowForm(true);
            }} onDelete={handleDelete} />

            {showForm && <BrandForm onClose={() => setShowForm(false)} onBrandAdded={fetchBrands} editBrand={editBrand} />}
        </div>
    );
};

export default BrandPage;
