import React, { useEffect, useState } from "react";
import { getProducts } from "../../services/productService";
import { useNavigate } from "react-router-dom";

const Home = () => {
    const [products, setProducts] = useState([]);
    const [searchQuery, setSearchQuery] = useState(""); // T√¨m ki·∫øm theo t√™n
    const [selectedBrand, setSelectedBrand] = useState(""); // L·ªçc theo th∆∞∆°ng hi·ªáu
    const [selectedCategory, setSelectedCategory] = useState(""); // L·ªçc theo lo·∫°i
    const [minPrice, setMinPrice] = useState(""); // Gi√° t·ªëi thi·ªÉu
    const [maxPrice, setMaxPrice] = useState(""); // Gi√° t·ªëi ƒëa
    const navigate = useNavigate();

    // Fetch products based on filters
    useEffect(() => {
        const fetchProducts = async () => {
            const filters = {
                name: searchQuery, // T√¨m ki·∫øm theo t√™n
                brand: selectedBrand, // Th∆∞∆°ng hi·ªáu
                category: selectedCategory, // Lo·∫°i
                minPrice: minPrice, // Gi√° t·ªëi thi·ªÉu
                maxPrice: maxPrice // Gi√° t·ªëi ƒëa
            };
            const data = await getProducts(filters);
            setProducts(data);
        };

        fetchProducts();
    }, [searchQuery, selectedBrand, selectedCategory, minPrice, maxPrice]); // When filters change, fetch data again

    // Add product to cart
    const addToCart = (product) => {
        const cart = JSON.parse(localStorage.getItem("cart")) || [];
        const existing = cart.find(item => item._id === product._id);

        if (existing) {
            existing.quantity += 1;
        } else {
            cart.push({ ...product, quantity: 1 });
        }

        localStorage.setItem("cart", JSON.stringify(cart));
        alert("üõí S·∫£n ph·∫©m ƒë√£ ƒë∆∞·ª£c th√™m v√†o gi·ªè h√†ng!");
    };

    // Handle search button click
    const handleSearch = () => {
        // Trigger fetch products with the current filters
        const filters = {
            name: searchQuery,
            brand: selectedBrand,
            category: selectedCategory,
            minPrice: minPrice,
            maxPrice: maxPrice
        };
        getProducts(filters).then(data => setProducts(data));
    };

    return (
        <div>
            <h2>Danh s√°ch s·∫£n ph·∫©m</h2>

            {/* T√¨m ki·∫øm v√† b·ªô l·ªçc */}
            <div>
                {/* T√¨m ki·∫øm theo t√™n s·∫£n ph·∫©m */}
                <input
                    type="text"
                    placeholder="T√¨m ki·∫øm theo t√™n s·∫£n ph·∫©m..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                
                {/* Brand filter */}
                <select onChange={(e) => setSelectedBrand(e.target.value)} value={selectedBrand}>
                    <option value="">Ch·ªçn Th∆∞∆°ng Hi·ªáu</option>
                    {/* Replace these options with dynamic data */}
                    <option value="brand1">Th∆∞∆°ng Hi·ªáu 1</option>
                    <option value="brand2">Th∆∞∆°ng Hi·ªáu 2</option>
                </select>
                
                {/* Category filter */}
                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                    <option value="">Ch·ªçn Lo·∫°i</option>
                    {/* Replace these options with dynamic data */}
                    <option value="category1">Lo·∫°i 1</option>
                    <option value="category2">Lo·∫°i 2</option>
                </select>
                
                {/* Price filter */}
                <input
                    type="number"
                    placeholder="Gi√° t·ªëi thi·ªÉu"
                    value={minPrice}
                    onChange={(e) => setMinPrice(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Gi√° t·ªëi ƒëa"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(e.target.value)}
                />

                {/* Button to trigger the search */}
                <button onClick={handleSearch}>T√¨m ki·∫øm</button>
            </div>

            {/* Hi·ªÉn th·ªã c√°c s·∫£n ph·∫©m */}
            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
                {products.map((product) => (
                    <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", width: "220px" }}>
                        <img
                            src={`http://localhost:5000${product.imageUrl}`}
                            alt={product.name}
                            style={{ width: "200px", height: "200px", objectFit: "cover" }}
                        />
                        <h3>{product.name}</h3>
                        <p><strong>ƒê√°nh gi√°:</strong> {product.rating?.toFixed(1) || 0} ‚≠ê</p>
                        <p><strong>Gi√°:</strong> {product.price} VND</p>
                        <button onClick={() => navigate(`/product/${product._id}`)}>Xem chi ti·∫øt</button>
                        <button style={{ marginTop: "5px" }} onClick={() => addToCart(product)}>üõí Mua</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
