import React, { useEffect, useState } from "react";
import { getProducts, createProduct, updateProduct, deleteProduct } from "../services/productService";

const Admin = () => {
    const [products, setProducts] = useState([]);
    const [newProduct, setNewProduct] = useState({ name: "", price: "", category: "", brand: "", stock: "" });
    const token = localStorage.getItem("token");

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        const data = await getProducts();
        setProducts(data);
    };

    const handleCreate = async () => {
        await createProduct(newProduct, token);
        fetchProducts();
    };

    const handleDelete = async (id) => {
        await deleteProduct(id, token);
        fetchProducts();
    };

    return (
        <div>
            <h1>Admin Panel</h1>
            <input placeholder="Product Name" onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })} />
            <input placeholder="Price" onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })} />
            <button onClick={handleCreate}>Add Product</button>
            <ul>
                {products.map((product) => (
                    <li key={product._id}>
                        {product.name} - ${product.price}
                        <button onClick={() => handleDelete(product._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Admin;
