import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { getProducts } from "../../services/productService";

const SearchResults = () => {
    const location = useLocation();
    const [products, setProducts] = useState([]);
    const [filters, setFilters] = useState(location.state || {}); // Receive filters via location.state

    useEffect(() => {
        const fetchProducts = async () => {
            const data = await getProducts(filters);
            setProducts(data);
        };

        fetchProducts();
    }, [filters]); // Fetch products whenever filters change

    return (
        <div>
            <h2>Danh sách sản phẩm</h2>
            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
                {products.length === 0 ? (
                    <p>Không có sản phẩm phù hợp với tiêu chí tìm kiếm.</p>
                ) : (
                    products.map((product) => (
                        <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", width: "220px" }}>
                            <img
                                src={`http://localhost:5000${product.imageUrl}`}
                                alt={product.name}
                                style={{ width: "200px", height: "200px", objectFit: "cover" }}
                            />
                            <h3>{product.name}</h3>
                            <p><strong>Đánh giá:</strong> {product.rating?.toFixed(1) || 0} ⭐</p>
                            <p><strong>Giá:</strong> {product.price} VND</p>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default SearchResults;
