import React, { useEffect, useState } from "react";
import { getProducts } from "../../services/productService";
import { useNavigate } from "react-router-dom";

const Home = () => {
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProducts = async () => {
            const data = await getProducts();
            setProducts(data);
        };
        fetchProducts();
    }, []);

    const addToCart = (product) => {
        const cart = JSON.parse(localStorage.getItem("cart")) || [];
        const existing = cart.find(item => item._id === product._id);

        if (existing) {
            existing.quantity += 1;
        } else {
            cart.push({ ...product, quantity: 1 });
        }

        localStorage.setItem("cart", JSON.stringify(cart));
        alert("üõí S·∫£n ph·∫©m ƒë√£ ƒë∆∞·ª£c th√™m v√†o gi·ªè h√†ng!");
    };

    return (
        <div>
            <h2>Danh s√°ch s·∫£n ph·∫©m</h2>
            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
                {products.map((product) => (
                    <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", width: "220px" }}>
                        <img
                            src={`http://localhost:5000${product.imageUrl}`}
                            alt={product.name}
                            style={{ width: "200px", height: "200px", objectFit: "cover" }}
                        />
                        <h3>{product.name}</h3>
                        <p><strong>ƒê√°nh gi√°:</strong> {product.rating?.toFixed(1) || 0} ‚≠ê</p>
                        <p><strong>Gi√°:</strong> {product.price} VND</p>
                        <button onClick={() => navigate(`/product/${product._id}`)}>Xem chi ti·∫øt</button>
                        <button style={{ marginTop: "5px" }} onClick={() => addToCart(product)}>üõí Mua</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
