import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
    getProducts,
    getBrands,
    getCategories
} from "../../services/productService";
import './css/HomeStyles.css';

const Home = () => {
    const [products, setProducts] = useState([]);
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedBrand, setSelectedBrand] = useState("");
    const [selectedCategory, setSelectedCategory] = useState("");
    const [minPrice, setMinPrice] = useState("");
    const [maxPrice, setMaxPrice] = useState("");
    const navigate = useNavigate();

    const userId = localStorage.getItem("userId");

    useEffect(() => {
        const fetchProducts = async () => {
            const filters = {
                name: searchQuery,
                brand: selectedBrand,
                category: selectedCategory,
                minPrice: minPrice,
                maxPrice: maxPrice
            };
            const data = await getProducts(filters);
            setProducts(data);
        };

        fetchProducts();
    }, [searchQuery, selectedBrand, selectedCategory, minPrice, maxPrice]);

    useEffect(() => {
        const fetchBrands = async () => {
            const data = await getBrands();
            setBrands(data);
        };
        const fetchCategories = async () => {
            const data = await getCategories();
            setCategories(data);
        };
        fetchBrands();
        fetchCategories();
    }, []);

    const addToCart = (product) => {
        if (!userId) {
            alert("‚ö† Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng.");
            return;
        }

        const cartKey = `cart_${userId}`;
        const cart = JSON.parse(localStorage.getItem(cartKey)) || [];
        const existing = cart.find(item => item._id === product._id);

        if (existing) {
            existing.quantity += 1;
        } else {
            cart.push({ ...product, quantity: 1 });
        }

        localStorage.setItem(cartKey, JSON.stringify(cart));
        alert("üõí S·∫£n ph·∫©m ƒë√£ ƒë∆∞·ª£c th√™m v√†o gi·ªè h√†ng!");
    };

    const handleSearch = () => {
        const filters = {
            name: searchQuery,
            brand: selectedBrand,
            category: selectedCategory,
            minPrice: minPrice,
            maxPrice: maxPrice
        };
        getProducts(filters).then(data => setProducts(data));
    };

    return (
        <div>
            <h2>Danh s√°ch s·∫£n ph·∫©m</h2>

            <div className="search-container">
                <input
                    type="text"
                    placeholder="T√¨m ki·∫øm theo t√™n s·∫£n ph·∫©m..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                <select onChange={(e) => setSelectedBrand(e.target.value)} value={selectedBrand}>
                    <option value="">Ch·ªçn Th∆∞∆°ng Hi·ªáu</option>
                    {brands.map((brand) => (
                        <option key={brand._id} value={brand._id}>
                            {brand.name}
                        </option>
                    ))}
                </select>
                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                    <option value="">Ch·ªçn Lo·∫°i</option>
                    {categories.map((category) => (
                        <option key={category._id} value={category._id}>
                            {category.name}
                        </option>
                    ))}
                </select>
                <input
                    type="number"
                    placeholder="Gi√° t·ªëi thi·ªÉu"
                    value={minPrice}
                    onChange={(e) => setMinPrice(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Gi√° t·ªëi ƒëa"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(e.target.value)}
                />
                <button onClick={handleSearch}>T√¨m ki·∫øm</button>
