import React, { useState, useEffect } from "react";
import { createProduct, updateProduct } from "../../../services/productService";
import { getBrands } from "../../../services/brandService";
import { getCategories } from "../../../services/categoryService";

const ProductForm = ({ onClose, onProductAdded, editProduct }) => {
    const [productData, setProductData] = useState({
        name: editProduct ? editProduct.name : "",
        price: editProduct ? editProduct.price : "",
        stock: editProduct ? editProduct.stock : "",
        description: editProduct ? editProduct.description : "",
        size: editProduct ? editProduct.size.join(", ") : "",
        colors: editProduct ? editProduct.colors.join(", ") : "",
        brand: editProduct ? editProduct.brand._id : "",
        category: editProduct ? editProduct.category._id : "",
        imageUrl: null,
    });

    const handleChange = (e) => setProductData({ ...productData, [e.target.name]: e.target.value });
    const handleFileChange = (e) => setProductData({ ...productData, imageUrl: e.target.files[0] });

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!productData.name || !productData.price || !productData.stock || !productData.brand || !productData.category) {
            return alert("Vui lòng nhập đầy đủ thông tin!");
        }

        const formData = new FormData();
        Object.keys(productData).forEach((key) => formData.append(key, productData[key]));

        try {
            editProduct ? await updateProduct(editProduct._id, formData) : await createProduct(formData);
            onProductAdded();
            onClose();
        } catch (error) {
            alert("Lỗi khi xử lý sản phẩm!");
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="name" value={productData.name} onChange={handleChange} placeholder="Product Name" />
            <input type="number" name="price" value={productData.price} onChange={handleChange} placeholder="Price" />
            <input type="file" onChange={handleFileChange} />
            <button type="submit">{editProduct ? "Update" : "Add"}</button>
            <button type="button" onClick={onClose}>Cancel</button>
        </form>
    );
};

export default ProductForm;
