import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
    getBrands, createBrand, updateBrand, deleteBrand
} from "../services/brandService";
import {
    getCategories, createCategory, updateCategory, deleteCategory
} from "../services/categoryService";
import {
    getProducts, createProduct, updateProduct, deleteProduct
} from "../services/productService";

const Admin = () => {
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [newBrand, setNewBrand] = useState("");
    const [newCategory, setNewCategory] = useState("");
    const [newProduct, setNewProduct] = useState({ name: "", price: "", category: "", brand: "", stock: "", description: "" });
    const token = localStorage.getItem("token");
    const navigate = useNavigate();

    useEffect(() => {
        fetchBrands();
        fetchCategories();
        fetchProducts();
    }, []);

    const fetchBrands = async () => {
        setBrands(await getBrands());
    };

    const fetchCategories = async () => {
        setCategories(await getCategories());
    };

    const fetchProducts = async () => {
        setProducts(await getProducts());
    };

    const handleCreateBrand = async () => {
        console.log("Token đang gửi: ", token); // Kiểm tra token có tồn tại không
        if (!token) {
            alert("Bạn chưa đăng nhập!");
            return;
        }
        try {
            await createBrand({ name: newBrand }, token);
            fetchBrands();
        } catch (error) {
            console.error("Lỗi khi tạo Brand:", error.response?.data || error.message);
        }
    };
    

    const handleUpdateBrand = async (id, newName) => {
        await updateBrand(id, { name: newName }, token);
        fetchBrands();
    };

    const handleCreateCategory = async () => {
        await createCategory({ name: newCategory }, token);
        fetchCategories();
    };

    const handleUpdateCategory = async (id, newName) => {
        await updateCategory(id, { name: newName }, token);
        fetchCategories();
    };

    const handleCreateProduct = async () => {
        await createProduct(newProduct, token);
        fetchProducts();
    };

    const handleUpdateProduct = async (id, updatedProduct) => {
        await updateProduct(id, updatedProduct, token);
        fetchProducts();
    };

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        navigate("/login");
    };

    return (
        <div>
            <h1>Admin Panel</h1>
            {token && <button onClick={handleLogout}>Logout</button>}
            
            <h2>Manage Brands</h2>
            <input placeholder="Brand Name" onChange={(e) => setNewBrand(e.target.value)} />
            <button onClick={handleCreateBrand}>Add Brand</button>
            <ul>
                {brands.map((brand) => (
                    <li key={brand._id}>
                        <input defaultValue={brand.name} onBlur={(e) => handleUpdateBrand(brand._id, e.target.value)} />
                        <button onClick={() => deleteBrand(brand._id, token)}>Delete</button>
                    </li>
                ))}
            </ul>

            <h2>Manage Categories</h2>
            <input placeholder="Category Name" onChange={(e) => setNewCategory(e.target.value)} />
            <button onClick={handleCreateCategory}>Add Category</button>
            <ul>
                {categories.map((category) => (
                    <li key={category._id}>
                        <input defaultValue={category.name} onBlur={(e) => handleUpdateCategory(category._id, e.target.value)} />
                        <button onClick={() => deleteCategory(category._id, token)}>Delete</button>
                    </li>
                ))}
            </ul>

            <h2>Manage Products</h2>
            <input placeholder="Product Name" onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })} />
            <button onClick={handleCreateProduct}>Add Product</button>
            <ul>
                {products.map((product) => (
                    <li key={product._id}>
                        <input defaultValue={product.name} onBlur={(e) => handleUpdateProduct(product._id, { name: e.target.value })} />
                        <button onClick={() => deleteProduct(product._id, token)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Admin;
