import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getBrands, createBrand, deleteBrand } from "../services/brandService";
import { getCategories, createCategory, deleteCategory } from "../services/categoryService";
import { getProducts, createProduct, updateProduct, deleteProduct } from "../services/productService";

const Admin = () => {
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [newBrand, setNewBrand] = useState("");
    const [newCategory, setNewCategory] = useState("");
    const [newProduct, setNewProduct] = useState({ name: "", price: "", category: "", brand: "", stock: "", description: "", size: "", colors: "", imageFile: null });
    const token = localStorage.getItem("token");
    const navigate = useNavigate();

    useEffect(() => {
        fetchBrands();
        fetchCategories();
        fetchProducts();
    }, []);

    const fetchBrands = async () => {
        const data = await getBrands();
        setBrands(data);
    };

    const fetchCategories = async () => {
        const data = await getCategories();
        setCategories(data);
    };

    const fetchProducts = async () => {
        const data = await getProducts();
        setProducts(data);
    };

    const handleCreateBrand = async () => {
        await createBrand({ name: newBrand }, token);
        fetchBrands();
    };

    const handleCreateCategory = async () => {
        await createCategory({ name: newCategory }, token);
        fetchCategories();
    };

    const handleCreateProduct = async () => {
        const formData = new FormData();
        formData.append("name", newProduct.name);
        formData.append("price", newProduct.price);
        formData.append("stock", newProduct.stock);
        formData.append("description", newProduct.description);
        formData.append("size", newProduct.size);
        formData.append("colors", newProduct.colors);
        formData.append("category", newProduct.category);
        formData.append("brand", newProduct.brand);
        formData.append("imageFile", newProduct.imageFile);
        await createProduct(formData, token);
        fetchProducts();
    };

    const handleDeleteProduct = async (id) => {
        await deleteProduct(id, token);
        fetchProducts();
    };

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        navigate("/login");
    };

    return (
        <div>
            <h1>Admin Panel</h1>
            {token && <button onClick={handleLogout}>Logout</button>}
            
            <h2>Manage Brands</h2>
            <input placeholder="Brand Name" onChange={(e) => setNewBrand(e.target.value)} />
            <button onClick={handleCreateBrand}>Add Brand</button>
            <ul>
                {brands.map((brand) => (
                    <li key={brand._id}>{brand.name} <button onClick={() => deleteBrand(brand._id, token)}>Delete</button></li>
                ))}
            </ul>
            
            <h2>Manage Categories</h2>
            <input placeholder="Category Name" onChange={(e) => setNewCategory(e.target.value)} />
            <button onClick={handleCreateCategory}>Add Category</button>
            <ul>
                {categories.map((category) => (
                    <li key={category._id}>{category.name} <button onClick={() => deleteCategory(category._id, token)}>Delete</button></li>
                ))}
            </ul>
            
            <h2>Manage Products</h2>
            <input placeholder="Product Name" onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })} />
            <input placeholder="Price" onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })} />
            <input placeholder="Stock" onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })} />
            <input placeholder="Description" onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })} />
            <input placeholder="Size (comma-separated)" onChange={(e) => setNewProduct({ ...newProduct, size: e.target.value.split(",") })} />
            <input placeholder="Colors (comma-separated)" onChange={(e) => setNewProduct({ ...newProduct, colors: e.target.value.split(",") })} />
            <input type="file" onChange={(e) => setNewProduct({ ...newProduct, imageFile: e.target.files[0] })} />
            
            <select onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}>
                <option value="">Select Category</option>
                {categories.map(category => <option key={category._id} value={category._id}>{category.name}</option>)}
            </select>
            
            <select onChange={(e) => setNewProduct({ ...newProduct, brand: e.target.value })}>
                <option value="">Select Brand</option>
                {brands.map(brand => <option key={brand._id} value={brand._id}>{brand.name}</option>)}
            </select>
            
            <button onClick={handleCreateProduct}>Add Product</button>
            
            <ul>
                {products.map((product) => (
                    <li key={product._id}>{product.name} - ${product.price} 
                        <button onClick={() => handleDeleteProduct(product._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Admin;
