import React, { useState } from "react";
import { createProduct, updateProduct } from "../../../services/productService";

const ProductForm = ({ onClose, onProductAdded, editProduct }) => {
    const [productData, setProductData] = useState({
        name: editProduct ? editProduct.name : "",
        price: editProduct ? editProduct.price : "",
        stock: editProduct ? editProduct.stock : "",
        category: editProduct ? editProduct.category : "",
        brand: editProduct ? editProduct.brand : "",
        imageUrl: null,
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!productData.name || !productData.price || !productData.stock || !productData.category || !productData.brand) {
            return alert("Vui lòng nhập đầy đủ thông tin sản phẩm!");
        }

        if (editProduct) {
            await updateProduct(editProduct._id, productData);
        } else {
            await createProduct(productData);
        }

        onProductAdded();
        onClose();
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h3>{editProduct ? "Edit Product" : "Add Product"}</h3>
                <form onSubmit={handleSubmit}>
                    <input type="text" placeholder="Product Name" value={productData.name} onChange={(e) => setProductData({ ...productData, name: e.target.value })} />
                    <input type="number" placeholder="Price" value={productData.price} onChange={(e) => setProductData({ ...productData, price: e.target.value })} />
                    <input type="number" placeholder="Stock" value={productData.stock} onChange={(e) => setProductData({ ...productData, stock: e.target.value })} />
                    <button type="submit">{editProduct ? "Update" : "Add"}</button>
                    <button type="button" className="close-btn" onClick={onClose}>❌ Cancel</button>
                </form>
            </div>
        </div>
    );
};

export default ProductForm;
