// frontend/pages/admin/OrderManagement.jsx
import React, { useEffect, useState } from "react";
import { getAllOrders } from "../../services/orderService"; // Make sure to import the service

const OrderManagement = () => {
    const [orders, setOrders] = useState([]);
    const [message, setMessage] = useState("");

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const ordersData = await getAllOrders(); // Fetch orders
                setOrders(ordersData); // Set orders state
            } catch (error) {
                console.error("Lỗi khi lấy đơn hàng", error);
            }
        };
        fetchOrders(); // Fetch orders on component mount
    }, []);

    const handleStatusChange = async (orderId, status) => {
        // Handle status update here
    };

    const handleDeleteOrder = async (orderId) => {
        // Handle order deletion here
    };

    return (
        <div>
            <h2>Quản lý đơn hàng</h2>
            {message && <p>{message}</p>}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Người dùng</th>
                        <th>Tổng tiền</th>
                        <th>Trạng thái</th>
                        <th>Cập nhật trạng thái</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(order => (
                        <tr key={order._id}>
                            <td>{order._id}</td>
                            <td>{order.userId.username}</td>
                            <td>{order.totalPrice} VND</td>
                            <td>{order.status}</td>
                            <td>
                                <select
                                    value={order.status}
                                    onChange={(e) => handleStatusChange(order._id, e.target.value)}
                                >
                                    <option value="Chờ xác nhận">Chờ xác nhận</option>
                                    <option value="Đã giao">Đã giao</option>
                                    <option value="Đang xử lý">Đang xử lý</option>
                                </select>
                            </td>
                            <td>
                                <button onClick={() => handleDeleteOrder(order._id)}>Xóa</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default OrderManagement;
