import React, { useEffect, useState } from "react";
import {
  createBrowserRouter,
  RouterProvider,
  Navigate,
  useNavigate,
} from "react-router-dom";

import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import Home from "./components/pages/home/Home";
import Dashboard from "./components/pages/admin/Dashboard";
import BrandPage from "./components/pages/admin/Brand/BrandPage";
import CategoryPage from "./components/pages/admin/Category/CategoryPage";
import ProductPage from "./components/pages/admin/Product/ProductPage";
import ProductDetail from "./components/pages/home/ProductDetail";
import ReviewPage from "./components/pages/admin/Review/ReviewPage";
import CartPage from "./components/pages/Cart/CartPage";
import OrderManagement from "./components/pages/admin/Order/OrderManagement";
import OrderHistory from "./components/pages/home/OrderHistory";
import SearchResults from "./components/pages/home/SearchResults";

// Navbar component (gi·ªØ nguy√™n logic b·∫°n c√≥)
const Navbar = ({ role, setRole }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    setRole("");
    navigate("/login");
  };

  return (
    <nav
      style={{
        display: "flex",
        justifyContent: "space-between",
        padding: "10px",
        background: "#ddd",
        alignItems: "center",
      }}
    >
      <div>
        <button onClick={() => navigate("/")}>üè† Home</button>
        {role === "admin" && (
          <button onClick={() => navigate("/admin")}>üîß Admin</button>
        )}
        {role === "user" && (
          <button onClick={() => navigate("/order-history")}>
            üìù L·ªãch s·ª≠ ƒë∆°n h√†ng
          </button>
        )}
      </div>
      <div>
        <button onClick={() => navigate("/cart")}>üõí Gi·ªè h√†ng</button>
        {role ? (
          <button onClick={handleLogout}>üö™ Logout</button>
        ) : (
          <>
            <button onClick={() => navigate("/login")}>üîë Login</button>
            <button onClick={() => navigate("/register")}>üìù Register</button>
          </>
        )}
      </div>
    </nav>
  );
};

// Wrapper ƒë·ªÉ t·∫°o route v√† inject Navbar v√†o t·ª´ng route
const AppWrapper = ({ role, setRole, cartItems, clearCart }) => {
  const withNavbar = (component) => (
    <>
      <Navbar role={role} setRole={setRole} />
      {component}
    </>
  );

  const router = createBrowserRouter(
    [
      { path: "/", element: withNavbar(<Home />) },
      { path: "/product/:id", element: withNavbar(<ProductDetail />) },
      {
        path: "/cart",
        element: withNavbar(
          <CartPage cartItems={cartItems} clearCart={clearCart} />
        ),
      },
      { path: "/order-history", element: withNavbar(<OrderHistory />) },
      { path: "/search-results", element: withNavbar(<SearchResults />) },
      { path: "/login", element: withNavbar(<Login setRole={setRole} />) },
      { path: "/register", element: withNavbar(<Register />) },

      // Admin routes (ch·ªâ hi·ªÉn th·ªã khi role l√† admin)
      {
        path: "/admin",
        element:
          role === "admin" ? withNavbar(<Dashboard />) : <Navigate to="/" />,
      },
      {
        path: "/admin/brands",
        element:
          role === "admin" ? withNavbar(<BrandPage />) : <Navigate to="/" />,
      },
      {
        path: "/admin/categories",
        element:
          role === "admin"
            ? withNavbar(<CategoryPage />)
            : <Navigate to="/" />,
      },
      {
        path: "/admin/products",
        element:
          role === "admin"
            ? withNavbar(<ProductPage />)
            : <Navigate to="/" />,
      },
      {
        path: "/admin/reviews",
        element:
          role === "admin" ? withNavbar(<ReviewPage />) : <Navigate to="/" />,
      },
      {
        path: "/admin/orders",
        element:
          role === "admin"
            ? withNavbar(<OrderManagement />)
            : <Navigate to="/" />,
      },

      // Fallback route
      { path: "*", element: <Navigate to="/" /> },
    ],
    {
      future: {
        v7_startTransition: true,
        v7_relativeSplatPath: true,
      },
    }
  );

  return <RouterProvider router={router} />;
};

// Main App component
const App = () => {
  const [role, setRole] = useState(localStorage.getItem("role") || "");
  const [cartItems, setCartItems] = useState(() => {
    return JSON.parse(localStorage.getItem("cart")) || [];
  });

  useEffect(() => {
    const handleStorageChange = () =>
      setRole(localStorage.getItem("role") || "");
    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const clearCart = () => {
    setCartItems([]);
    localStorage.removeItem("cart");
  };

  return (
    <AppWrapper
      role={role}
      setRole={setRole}
      cartItems={cartItems}
      clearCart={clearCart}
    />
  );
};

export default App;
