import React from "react";
import { useNavigate, Link } from "react-router-dom";
import "./Home.css"; // Optional CSS if you want to style

const Home = () => {
    const navigate = useNavigate();
    const role = localStorage.getItem("role");

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        navigate("/login");
    };

    // Dummy product data (replace with real API later)
    const products = [
        {
            id: 1,
            name: "Nike Air Force 1",
            price: "$120",
            image: "https://static.nike.com/a/images/t_PDP_1280_v1/f_auto,q_auto:eco/fce4b2e1-2e03-45e1-a2cd-33be74b27d4c/air-force-1-07-shoes-GjGXSP.png"
        },
        {
            id: 2,
            name: "Adidas Ultraboost",
            price: "$150",
            image: "https://assets.adidas.com/images/h_840,f_auto,q_auto:sensitive,fl_lossy/6c514a7a67ab4529b7e4af0400e9f712_9366/Ultraboost_21_Shoes_White_S23869_01_standard.jpg"
        },
        {
            id: 3,
            name: "Converse Chuck Taylor",
            price: "$70",
            image: "https://images.ctfassets.net/od02wyo8cgm5/3t3NKVKzzrKKcqAsDd3ceP/5dfb3ab9c5ff8c46b972d937e6f1fc8b/converse-chuck-taylor-all-star-classic-high-top-black.jpg"
        }
    ];

    return (
        <div className="home-container">
            <header className="home-header">
                <h1>Welcome to the Shoe Store</h1>
                <div className="auth-buttons">
                    {!role ? (
                        <>
                            <Link to="/login"><button>Đăng Nhập</button></Link>
                            <Link to="/register"><button>Đăng Ký</button></Link>
                        </>
                    ) : (
                        <button onClick={handleLogout}>Logout</button>
                    )}
                </div>
            </header>

            <section className="product-list">
                {products.map((product) => (
                    <div key={product.id} className="product-card">
                        <img src={product.image} alt={product.name} />
                        <h3>{product.name}</h3>
                        <p>{product.price}</p>
                        <button>Mua ngay</button>
                    </div>
                ))}
            </section>
        </div>
    );
};

export default Home;
