import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ReviewStyles.css"; // üëà File CSS t√πy ch·ªânh (t·∫°o n·∫øu ch∆∞a c√≥)

const ReviewPage = () => {
    const [reviews, setReviews] = useState([]);

    const fetchReviews = async () => {
        try {
            const token = localStorage.getItem("token");
            const res = await axios.get("http://localhost:5000/api/reviews", {
                headers: { Authorization: `Bearer ${token}` }
            });
            setReviews(res.data);
        } catch (err) {
            console.error("‚ùå L·ªói khi l·∫•y ƒë√°nh gi√°:", err.response?.data || err.message);
        }
    };

    const handleDelete = async (reviewId) => {
        if (!window.confirm("B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën x√≥a ƒë√°nh gi√° n√†y?")) return;

        try {
            const token = localStorage.getItem("token");
            await axios.delete(`http://localhost:5000/api/reviews/${reviewId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setReviews(reviews.filter(r => r._id !== reviewId)); // C·∫≠p nh·∫≠t UI
        } catch (err) {
            console.error("‚ùå L·ªói khi x√≥a ƒë√°nh gi√°:", err.response?.data || err.message);
        }
    };

    useEffect(() => {
        fetchReviews();
    }, []);

    return (
        <div className="review-container">
            <h2>üìã Qu·∫£n l√Ω ƒë√°nh gi√°</h2>
            <table className="review-table">
                <thead>
                    <tr>
                        <th>S·∫£n ph·∫©m</th>
                        <th>Ng∆∞·ªùi d√πng</th>
                        <th>ƒê√°nh gi√°</th>
                        <th>N·ªôi dung</th>
                        <th>Th·ªùi gian</th>
                        <th>H√†nh ƒë·ªông</th>
                    </tr>
                </thead>
                <tbody>
                    {reviews.length === 0 ? (
                        <tr><td colSpan="6">Ch∆∞a c√≥ ƒë√°nh gi√° n√†o.</td></tr>
                    ) : (
                        reviews.map((r) => (
                            <tr key={r._id}>
                                <td>{r.product?.name || "Kh√¥ng x√°c ƒë·ªãnh"}</td>
                                <td>{r.user?.username || "·∫®n danh"}</td>
                                <td>{r.rating} ‚≠ê</td>
                                <td>{r.comment}</td>
                                <td>{new Date(r.createdAt).toLocaleString()}</td>
                                <td>
                                    <button onClick={() => handleDelete(r._id)}>‚ùå X√≥a</button>
                                </td>
                            </tr>
                        ))
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ReviewPage;
