import React from "react";
import { Link, useNavigate } from "react-router-dom";
import "./css/Dashboard.css"; // üëà Import file CSS ri√™ng

const Dashboard = () => {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        localStorage.removeItem("userId");
        localStorage.removeItem("username");
        navigate("/login");
    };

    return (
        <div className="dashboard-container container mt-5">
            <div className="card shadow p-4">
                <h1 className="text-center mb-4">Admin Dashboard</h1>
                <ul className="list-group list-group-flush mb-3">
                    <li className="list-group-item">
                        <Link className="nav-link" to="/admin/brands">üì¶ Manage Brands</Link>
                    </li>
                    <li className="list-group-item">
                        <Link className="nav-link" to="/admin/categories">üìÇ Manage Categories</Link>
                    </li>
                    <li className="list-group-item">
                        <Link className="nav-link" to="/admin/products">üõí Manage Products</Link>
                    </li>
                    <li className="list-group-item">
                        <Link className="nav-link" to="/admin/reviews">‚≠ê Manage Reviews</Link>
                    </li>
                    <li className="list-group-item">
                        <Link className="nav-link" to="/admin/orders">üìë Manage Orders</Link>
                    </li>
                </ul>
                <div className="text-center">
                    <button className="btn btn-danger" onClick={handleLogout}>üö™ Logout</button>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
