import React from "react";
import { deleteProduct } from "../../../services/productService";

const ProductList = ({ products, onEdit, onDelete }) => {
    return (
        <table className="product-table">
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Price</th>
                    <th>Stock</th>
                    <th>Description</th>
                    <th>Size</th>
                    <th>Colors</th>
                    <th>Image</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {products.map((product) => (
                    <tr key={product._id}>
                        <td>{product.name}</td>
                        <td>{product.price}</td>
                        <td>{product.stock}</td>
                        <td>{product.description}</td>
                        <td>{product.size.join(", ")}</td>
                        <td>{product.colors.join(", ")}</td>
                        <td>
                            {product.imageUrl ? <img src={product.imageUrl} alt={product.name} width="50" /> : "No Image"}
                        </td>
                        <td>
                            <button className="edit-btn" onClick={() => onEdit(product)}>‚úèÔ∏è Edit</button>
                            <button className="delete-btn" onClick={() => onDelete(product._id)}>üóëÔ∏è Delete</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default ProductList;
