import React, { useState, useEffect } from "react";
import { createOrderWithDetails } from "../../services/orderService";

const CartPage = () => {
    const userId = localStorage.getItem("userId");
    const [cartItems, setCartItems] = useState([]);
    const [selectedItems, setSelectedItems] = useState([]);
    const [message, setMessage] = useState("");

    const [fullName, setFullName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [address, setAddress] = useState("");
    const [note, setNote] = useState("");

    useEffect(() => {
        if (!userId) return;
        const storedCart = JSON.parse(localStorage.getItem(`cart_${userId}`)) || [];
        setCartItems(storedCart);
        setSelectedItems(storedCart.map(item => item._id));
    }, [userId]);

    const updateCart = (newCart) => {
        setCartItems(newCart);
        localStorage.setItem(`cart_${userId}`, JSON.stringify(newCart));
    };

    const toggleItemSelection = (id) => {
        setSelectedItems(prev =>
            prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
        );
    };

    const handleQuantityChange = (id, delta) => {
        const updatedCart = cartItems.map(item =>
            item._id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item
        );
        updateCart(updatedCart);
    };

    const handleRemoveItem = (id) => {
        const updatedCart = cartItems.filter(item => item._id !== id);
        updateCart(updatedCart);
        setSelectedItems(prev => prev.filter(itemId => itemId !== id));
    };

    const handleCheckout = async () => {
        try {
            const itemsToOrder = cartItems.filter(item => selectedItems.includes(item._id));

            if (!fullName || !email || !phone || !address) {
                setMessage("‚ùå Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin kh√°ch h√†ng.");
                return;
            }

            const orderData = {
                items: itemsToOrder.map(item => ({
                    productId: item._id,
                    quantity: item.quantity,
                    price: item.price
                })),
                fullName,
                email,
                phone,
                address,
                note
            };

            await createOrderWithDetails(orderData);
            setMessage("‚úÖ ƒê·∫∑t h√†ng th√†nh c√¥ng!");

            const newCart = cartItems.filter(item => !selectedItems.includes(item._id));
            updateCart(newCart);
            setSelectedItems([]);

            setFullName("");
            setEmail("");
            setPhone("");
