import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getProductById } from "../../services/productService";
import "./ProductDetail.css"; 

const ProductDetail = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        fetchProduct();
    }, []);

    const fetchProduct = async () => {
        try {
            const data = await getProductById(id);
            setProduct(data);
        } catch (error) {
            console.error("Lỗi khi lấy chi tiết sản phẩm:", error);
        }
    };

    if (!product) {
        return <p>Loading...</p>;
    }

    return (
        <div className="product-detail-container">
            <h2>{product.name}</h2>
            <img src={`http://localhost:5000${product.imageUrl[0]}`} alt={product.name} className="product-detail-image" />
            <p><strong>Giá:</strong> {product.price} VND</p>
            <p><strong>Mô tả:</strong> {product.description}</p>
            <p><strong>Kích cỡ:</strong> {product.size.join(", ")}</p>
            <p><strong>Màu sắc:</strong> {product.colors.join(", ")}</p>
            <button onClick={() => navigate(`/cart/add/${product._id}`)}>Mua ngay</button>
            <button onClick={() => navigate("/")}>Quay lại</button>
        </div>
    );
};

export default ProductDetail;
