import React, { useEffect, useState } from "react";
import { getProducts } from "../../services/productService";
import { useNavigate } from "react-router-dom";
import "./HomeStyles.css"; // CSS cho giao diện Home

const Home = () => {
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const data = await getProducts();
            setProducts(data);
        } catch (error) {
            console.error("Lỗi khi lấy danh sách sản phẩm:", error);
        }
    };

    return (
        <div className="home-container">
            <h2>Danh sách sản phẩm</h2>
            <div className="product-list">
                {products.map((product) => (
                    <div key={product._id} className="product-card">
                        <img src={`http://localhost:5000${product.imageUrl[0]}`} alt={product.name} className="product-image" />
                        <h3>{product.name}</h3>
                        <p>Giá: {product.price} VND</p>
                        <button onClick={() => navigate(`/home/product/${product._id}`)}>Xem chi tiết</button>
                        <button onClick={() => navigate(`/cart/add/${product._id}`)}>Mua ngay</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
