import React from "react";
import { useNavigate } from "react-router-dom";
//import "./Admin.css"; // optional styling file

const Admin = () => {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        navigate("/login");
    };

    return (
        <div className="admin-dashboard">
            <aside className="sidebar">
                <h2>Admin Panel</h2>
                <ul>
                    <li>Dashboard</li>
                    <li>Manage Products</li>
                    <li>Orders</li>
                    <li>Users</li>
                    <li><button onClick={handleLogout}>Logout</button></li>
                </ul>
            </aside>

            <main className="main-content">
                <h1>Welcome, Admin!</h1>
                <p>This is your dashboard overview. From here, you can manage all aspects of the store.</p>

                {/* Example content */}
                <div className="admin-widgets">
                    <div className="widget">Total Orders: 128</div>
                    <div className="widget">New Users: 24</div>
                    <div className="widget">Products: 73</div>
                </div>
            </main>
        </div>
    );
};

export default Admin;
