// ‚úÖ frontend/src/components/pages/Cart/CartPage.jsx
import React, { useEffect, useState } from "react";
import { createOrderWithDetails } from "../../services/orderService";

const CartPage = () => {
  const userId = localStorage.getItem("userId");
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [message, setMessage] = useState("");
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [note, setNote] = useState("");

  useEffect(() => {
    if (!userId) return;
    const stored = JSON.parse(localStorage.getItem(`cart_${userId}`)) || [];
    setCartItems(stored);
    setSelectedItems(stored.map(item => item._id));
  }, [userId]);

  const updateCart = (items) => {
    setCartItems(items);
    localStorage.setItem(`cart_${userId}`, JSON.stringify(items));
  };

  const handleQuantityChange = (id, delta) => {
    const updated = cartItems.map(item =>
      item._id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item
    );
    updateCart(updated);
  };

  const removeItem = (id) => {
    const updated = cartItems.filter(item => item._id !== id);
    updateCart(updated);
    setSelectedItems(prev => prev.filter(i => i !== id));
  };

  const handleCheckout = async () => {
    if (!userId) return alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ thanh to√°n");
    if (!fullName || !email || !phone || !address) return setMessage("Vui l√≤ng ƒëi·ªÅn ƒë·ªß th√¥ng tin kh√°ch h√†ng");

    const itemsToCheckout = cartItems.filter(i => selectedItems.includes(i._id));
    const orderData = {
      items: itemsToCheckout.map(i => ({
        productId: i._id,
        quantity: i.quantity,
        price: i.price
      })),
      fullName, email, phone, address, note
    };

    try {
      await createOrderWithDetails(orderData);
      setMessage("‚úÖ ƒê·∫∑t h√†ng th√†nh c√¥ng!");
      const newCart = cartItems.filter(item => !selectedItems.includes(item._id));
      updateCart(newCart);
      setSelectedItems([]);
    } catch (e) {
      setMessage("‚ùå L·ªói khi ƒë·∫∑t h√†ng: " + (e.message || "Kh√¥ng x√°c ƒë·ªãnh"));
    }
  };

  if (!userId) return <p>‚ö† B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ s·ª≠ d·ª•ng gi·ªè h√†ng.</p>;

  return (
    <div style={{ padding: 20 }}>
      <h2>üõí Gi·ªè h√†ng</h2>
      {cartItems.length === 0 ? (
        <p>Gi·ªè h√†ng tr·ªëng</p>
      ) : (
        cartItems.map(item => (
          <div key={item._id}>
            <input
              type="checkbox"
              checked={selectedItems.includes(item._id)}
              onChange={() =>
                setSelectedItems(prev =>
                  prev.includes(item._id)
                    ? prev.filter(i => i !== item._id)
                    : [...prev, item._id]
                )
              }
            />
            {item.name} - {item.quantity} x {item.price} VND
            <button onClick={() => handleQuantityChange(item._id, 1)}>+</button>
            <button onClick={() => handleQuantityChange(item._id, -1)}>-</button>
            <button onClick={() => removeItem(item._id)}>üóë X√≥a</button>
          </div>
        ))
      )}

      {selectedItems.length > 0 && (
        <>
          <h3>üìã Th√¥ng tin kh√°ch h√†ng</h3>
          <input placeholder="H·ªç t√™n" value={fullName} onChange={(e) => setFullName(e.target.value)} />
          <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input placeholder="SƒêT" value={phone} onChange={(e) => setPhone(e.target.value)} />
          <input placeholder="ƒê·ªãa ch·ªâ" value={address} onChange={(e) => setAddress(e.target.value)} />
          <textarea placeholder="Ghi ch√∫" value={note} onChange={(e) => setNote(e.target.value)}></textarea>
          <button onClick={handleCheckout}>Thanh to√°n ({selectedItems.length} sp)</button>
        </>
      )}

      {message && <p>{message}</p>}
    </div>
  );
};

export default CartPage;
