import React, { useEffect, useState } from "react";
import { getProducts } from "../../services/productService";
import { useNavigate } from "react-router-dom";

const Home = () => {
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProducts = async () => {
            const data = await getProducts();
            setProducts(data);
        };
        fetchProducts();
    }, []);

    // üü¢ H√†m x·ª≠ l√Ω ƒêƒÉng xu·∫•t
    const handleLogout = () => {
        localStorage.removeItem("token"); // X√≥a token kh·ªèi localStorage
        localStorage.removeItem("role"); // X√≥a role ƒë·ªÉ tr√°nh l·ªói quy·ªÅn h·∫°n
        navigate("/login"); // Chuy·ªÉn h∆∞·ªõng v·ªÅ trang ƒëƒÉng nh·∫≠p
    };

    return (
        <div>
            {/* üü¢ N√∫t ƒêƒÉng xu·∫•t */}
            <button onClick={handleLogout} style={{ float: "right", margin: "10px", padding: "8px 12px", backgroundColor: "red", color: "white", border: "none", cursor: "pointer" }}>
                ƒêƒÉng xu·∫•t
            </button>

            <h2>Danh s√°ch s·∫£n ph·∫©m</h2>
            <div>
                {products.map((product) => (
                    <div key={product._id}>
                        <img src={`http://localhost:5000${product.imageUrl}`} alt={product.name} />
                        <h3>{product.name}</h3>
                        <p>Gi√°: {product.price} VND</p>
                        <button onClick={() => navigate(`/product/${product._id}`)}>Xem chi ti·∫øt</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
