import axios from "axios";

const API_URL = "http://localhost:5000/api/orders";

// üõí ƒê·∫∑t h√†ng c∆° b·∫£n (ch·ªâ v·ªõi userId v√† items)
export const createOrder = async (orderData) => {
    const token = localStorage.getItem("token");
    if (!token) return console.error("‚ùå Kh√¥ng c√≥ token");

    try {
        const res = await axios.post(API_URL, orderData, {
            headers: {
                Authorization: `Bearer ${token}`,
                "Content-Type": "application/json"
            }
        });
        return res.data;
    } catch (error) {
        throw error.response?.data || error.message;
    }
};

// üßæ ƒê·∫∑t h√†ng k√®m th√¥ng tin kh√°ch h√†ng (OrderDetail)
export const createOrderWithDetails = async (orderData) => {
    const token = localStorage.getItem("token");
    if (!token) return console.error("‚ùå Kh√¥ng c√≥ token");

    try {
        const res = await axios.post(`${API_URL}/with-details`, orderData, {
            headers: {
                Authorization: `Bearer ${token}`,
                "Content-Type": "application/json"
            }
        });
        return res.data;
    } catch (error) {
        throw error.response?.data || error.message;
    }
};

// üë§ L·∫•y ƒë∆°n h√†ng c·ªßa ng∆∞·ªùi d√πng
export const getMyOrders = async () => {
    const token = localStorage.getItem("token");
    if (!token) return [];

    const res = await axios.get(`${API_URL}/my`, {
        headers: { Authorization: `Bearer ${token}` }
    });

    return res.data;
};

// üîß L·∫•y to√†n b·ªô ƒë∆°n h√†ng (admin)
export const getAllOrders = async () => {
    const token = localStorage.getItem("token");
    if (!token) return [];

    const res = await axios.get(`${API_URL}/all`, {
        headers: { Authorization: `Bearer ${token}` }
    });

    return res.data;
};

// üìù C·∫≠p nh·∫≠t tr·∫°ng th√°i ƒë∆°n h√†ng
export const updateOrderStatus = async (orderId, status) => {
    const token = localStorage.getItem("token");
    if (!token) return console.error("‚ùå Kh√¥ng c√≥ token");

    try {
        const res = await axios.put(
            `${API_URL}/update-status`,
            { orderId, status },
            {
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json"
                }
            }
        );
        return res.data;
    } catch (error) {
        throw error.response?.data || error.message;
    }
};

// ‚ùå X√≥a ƒë∆°n h√†ng
export const deleteOrder = async (orderId) => {
    const token = localStorage.getItem("token");
    if (!token) return console.error("‚ùå Kh√¥ng c√≥ token");

    try {
        const res = await axios.delete(`${API_URL}/${orderId}`, {
            headers: {
                Authorization: `Bearer ${token}`,
                "Content-Type": "application/json"
            }
        });
        return res.data;
    } catch (error) {
        throw error.response?.data || error.message;
    }
};
