import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ReviewStyles.css"; // üëâ Nh·ªõ import CSS

const ReviewPage = () => {
    const [reviews, setReviews] = useState([]);

    const fetchReviews = async () => {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/products/reviews/all", {
            headers: { Authorization: `Bearer ${token}` },
        });
        setReviews(res.data);
    };

    const handleDelete = async (productId, reviewId) => {
        const token = localStorage.getItem("token");
        await axios.delete(`http://localhost:5000/api/products/${productId}/reviews/${reviewId}`, {
            headers: { Authorization: `Bearer ${token}` },
        });
        fetchReviews(); // refresh
    };

    useEffect(() => {
        fetchReviews();
    }, []);

    return (
        <div className="review-container">
            <div className="review-header">
                <h2>Qu·∫£n l√Ω ƒë√°nh gi√°</h2>
            </div>
            <table className="review-table">
                <thead>
                    <tr>
                        <th>S·∫£n ph·∫©m</th>
                        <th>Ng∆∞·ªùi d√πng</th>
                        <th>ƒê√°nh gi√°</th>
                        <th>N·ªôi dung</th>
                        <th>H√†nh ƒë·ªông</th>
                    </tr>
                </thead>
                <tbody>
                    {reviews.map((r) => (
                        <tr key={r._id}>
                            <td>{r.productName}</td>
                            <td>{r.userId?.username || "·∫®n danh"}</td>
                            <td>{r.rating} ‚≠ê</td>
                            <td>{r.comment}</td>
                            <td>
                                <button onClick={() => handleDelete(r.productId, r._id)}>‚ùå X√≥a</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ReviewPage;
