import React, { useState, useEffect } from "react";
import { createOrderWithDetails } from "../../services/orderService";

const CartPage = () => {
    const [cartItems, setCartItems] = useState([]);
    const [selectedItems, setSelectedItems] = useState([]);
    const [message, setMessage] = useState("");

    // Thông tin khách hàng
    const [fullName, setFullName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [address, setAddress] = useState("");
    const [note, setNote] = useState("");

    useEffect(() => {
        const cart = JSON.parse(localStorage.getItem("cart")) || [];
        setCartItems(cart);
        setSelectedItems(cart.map(item => item._id));
    }, []);

    const toggleItemSelection = (id) => {
        setSelectedItems(prev =>
            prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
        );
    };

    const handleQuantityChange = (id, delta) => {
        const updatedCart = cartItems.map(item =>
            item._id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item
        );
        setCartItems(updatedCart);
        localStorage.setItem("cart", JSON.stringify(updatedCart));
    };

    const handleCheckout = async () => {
        try {
            const itemsToOrder = cartItems.filter(item => selectedItems.includes(item._id));

            if (!fullName || !email || !phone || !address) {
                setMessage("❌ Vui lòng nhập đầy đủ thông tin khách hàng.");
                return;
            }

            const orderData = {
                items: itemsToOrder.map(item => ({
                    productId: item._id,
                    quantity: item.quantity,
                    price: item.price
                })),
                fullName,
                email,
                phone,
                address,
                note
            };

            await createOrderWithDetails(orderData);
            setMessage("✅ Đặt hàng thành công!");

            // Xoá sản phẩm đã đặt khỏi giỏ hàng
            const newCart = cartItems.filter(item => !selectedItems.includes(item._id));
            setCartItems(newCart);
            setSelectedItems([]);
            localStorage.setItem("cart", JSON.stringify(newCart));

            // Reset form
            setFullName("");
            setEmail("");
            setPhone("");
            setAddress("");
            setNote("");
        } catch (error) {
            setMessage("❌ Lỗi khi đặt hàng: " + (error.message || "Không xác định"));
        }
    };

    const totalSelectedPrice = cartItems
        .filter(item => selectedItems.includes(item._id))
        .reduce((sum, item) => sum + item.price * item.quantity, 0);

    return (
        <div style={{ padding: "20px" }}>
            <h2>🛒 Giỏ hàng</h2>

            {cartItems.length === 0 ? (
                <p>Giỏ hàng trống</p>
            ) : (
                <ul>
                    {cartItems.map(item => (
                        <li key={item._id} style={{ marginBottom: "10px", borderBottom: "1px solid #ccc", paddingBottom: "10px" }}>
                            <input
                                type="checkbox"
                                checked={selectedItems.includes(item._id)}
                                onChange={() => toggleItemSelection(item._id)}
                            />
                            <img src={`http://localhost:5000${item.imageUrl}`} alt={item.name} style={{ width: "60px", marginRight: "10px" }} />
                            <strong>{item.name}</strong> <br />
                            Giá: {item.price} VND <br />
                            Số lượng:{" "}
                            <button onClick={() => handleQuantityChange(item._id, -1)}>-</button>
                            {item.quantity}
                            <button onClick={() => handleQuantityChange(item._id, 1)}>+</button>
                        </li>
                    ))}
                </ul>
            )}

            <h4>Tổng tiền: {totalSelectedPrice.toLocaleString()} VND</h4>

            {selectedItems.length > 0 && (
                <div style={{ marginTop: "20px" }}>
                    <h3>📋 Thông tin khách hàng</h3>
                    <input placeholder="Họ tên" value={fullName} onChange={e => setFullName(e.target.value)} required /><br />
                    <input placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required /><br />
                    <input placeholder="Số điện thoại" value={phone} onChange={e => setPhone(e.target.value)} required /><br />
                    <input placeholder="Địa chỉ" value={address} onChange={e => setAddress(e.target.value)} required /><br />
                    <textarea placeholder="Ghi chú (tuỳ chọn)" value={note} onChange={e => setNote(e.target.value)}></textarea><br />

                    <button onClick={handleCheckout}>Thanh toán ({selectedItems.length} sản phẩm)</button>
                </div>
            )}

            {message && <p>{message}</p>}
        </div>
    );
};

export default CartPage;
