// frontend/src/components/pages/admin/Order/OrderManagement.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { updateOrderStatus, deleteOrder } from "../../../services/orderService";
import { deleteOrderDetail, updateOrderDetail } from "../../../services/orderDetailService";
import "../Order/OrderManagement.css";

const OrderManagement = () => {
    const [orders, setOrders] = useState([]);
    const [message, setMessage] = useState("");
    const [editingOrderDetail, setEditingOrderDetail] = useState(null); 

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/orders/all", {
                    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
                });
                setOrders(response.data);
            } catch (error) {
                console.error("Lỗi khi lấy đơn hàng", error);
                setMessage("Lỗi khi lấy đơn hàng");
            }
        };
        fetchOrders();
    }, []);

    const handleStatusChange = async (orderId, status) => {
        try {
            const response = await updateOrderStatus(orderId, status);
            setMessage(response.message);
            setOrders(orders.map(order => (order._id === orderId ? { ...order, status } : order)));
        } catch (error) {
            setMessage("Lỗi khi cập nhật trạng thái đơn hàng");
        }
    };

    const handleDeleteOrder = async (orderId) => {
        try {
            const response = await deleteOrder(orderId);
            setMessage(response.message);
            setOrders(orders.filter(order => order._id !== orderId));
        } catch (error) {
            setMessage("Lỗi khi xóa đơn hàng");
        }
    };

    const handleDeleteOrderDetail = async (orderDetailId) => {
        try {
            const response = await deleteOrderDetail(orderDetailId);
            setMessage(response.message);
            setOrders(orders.map(order => {
                if (order.orderDetails) {
                    order.orderDetails = order.orderDetails.filter(detail => detail._id !== orderDetailId);
                }
                return order;
            }));
        } catch (error) {
            setMessage("Lỗi khi xóa chi tiết đơn hàng");
        }
    };

    const handleUpdateOrderDetail = async (orderDetailId, updatedData) => {
        try {
            const response = await updateOrderDetail(orderDetailId, updatedData);
            setMessage(response.message);
            setOrders(orders.map(order => {
                if (order.orderDetails) {
                    order.orderDetails = order.orderDetails.map(detail => detail._id === orderDetailId ? response.data : detail);
                }
                return order;
            }));
            setEditingOrderDetail(null); 
        } catch (error) {
            setMessage("Lỗi khi cập nhật thông tin đơn hàng");
        }
    };

    return (
        <div className="order-container">
            <h2>Quản lý đơn hàng</h2>
            {message && <p className="order-message">{message}</p>}
            <table className="order-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Người dùng</th>
                        <th>Tổng tiền</th>
                        <th>Trạng thái</th>
                        <th>Cập nhật trạng thái</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(order => (
                        <tr key={order._id}>
                            <td>{order._id}</td>
                            <td>{order.userId.username}</td>
                            <td>{order.totalPrice} VND</td>
                            <td>{order.status}</td>
                            <td>
                                <select
                                    value={order.status}
                                    onChange={(e) => handleStatusChange(order._id, e.target.value)}
                                >
                                    <option value="Chờ xác nhận">Chờ xác nhận</option>
                                    <option value="Đang giao">Đang giao</option>
                                    <option value="Hoàn thành">Hoàn thành</option>
                                    <option value="Đã hủy">Đã hủy</option>
                                </select>
                            </td>
                            <td>
                                <button onClick={() => handleDeleteOrder(order._id)}>Xóa</button>
                                <button onClick={() => setEditingOrderDetail(order._id)}>Sửa</button> 
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {editingOrderDetail && (
                <div className="order-detail-form">
                    <h3>Sửa thông tin đơn hàng</h3>
                    <input type="text" placeholder="Tên" onChange={(e) => setEditingOrderDetail({ ...editingOrderDetail, fullName: e.target.value })} />
                    <input type="text" placeholder="Số điện thoại" onChange={(e) => setEditingOrderDetail({ ...editingOrderDetail, phone: e.target.value })} />
                    <button onClick={() => handleUpdateOrderDetail(editingOrderDetail._id, editingOrderDetail)}>Lưu thay đổi</button>
                    <button onClick={() => setEditingOrderDetail(null)}>Hủy</button>
                </div>
            )}
        </div>
    );
};

export default OrderManagement;
