import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getProducts, getBrands, getCategories } from "../../services/productService";
import './css/HomeStyles.css'; // CSS

const Home = () => {
    const [products, setProducts] = useState([]);
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [searchQuery, setSearchQuery] = useState(""); 
    const [selectedBrand, setSelectedBrand] = useState(""); 
    const [selectedCategory, setSelectedCategory] = useState(""); 
    const [minPrice, setMinPrice] = useState(""); 
    const [maxPrice, setMaxPrice] = useState(""); 

    const navigate = useNavigate();

    useEffect(() => {
        const fetchProducts = async () => {
            const filters = {
                name: searchQuery, 
                brand: selectedBrand, 
                category: selectedCategory, 
                minPrice: minPrice, 
                maxPrice: maxPrice 
            };
            const data = await getProducts(filters);
            setProducts(data);
        };

        fetchProducts();
    }, [searchQuery, selectedBrand, selectedCategory, minPrice, maxPrice]);

    useEffect(() => {
        const fetchBrands = async () => {
            const data = await getBrands();
            setBrands(data);
        };
        const fetchCategories = async () => {
            const data = await getCategories();
            setCategories(data);
        };
        fetchBrands();
        fetchCategories();
    }, []);

    const addToCart = (product) => {
        const token = localStorage.getItem("token");
        const userId = localStorage.getItem("userId");

        if (!token || !userId) {
            alert("⚠ Bạn cần đăng nhập để thêm sản phẩm vào giỏ hàng.");
            navigate("/login");
            return;
        }

        const cartKey = `cart_${userId}`;
        const cart = JSON.parse(localStorage.getItem(cartKey)) || [];
        const existingIndex = cart.findIndex(item => item._id === product._id);

        if (existingIndex !== -1) {
            cart[existingIndex].quantity += 1;
        } else {
            cart.push({ ...product, quantity: 1 });
        }

        localStorage.setItem(cartKey, JSON.stringify(cart));
        alert("🛒 Sản phẩm đã được thêm vào giỏ hàng!");
    };

  // Gửi filter sang trang SearchResults
  const handleSearch = () => {
    let min = "";
    let max = "";

    if (priceRange === "0-500000") {
        min = 0;
        max = 500000;
    } else if (priceRange === "500000-1000000") {
        min = 500000;
        max = 1000000;
    }

    const filters = {
        name: searchQuery,
        brand: selectedBrand,
        category: selectedCategory,
        minPrice: min,
        maxPrice: max
    };

    navigate("/search", { state: filters });
};


    return (
        <div>
            <h2>Danh sách sản phẩm</h2>

            {/* Tìm kiếm và bộ lọc */}
            <div className="search-container">
                <input
                    type="text"
                    placeholder="Tìm kiếm theo tên sản phẩm..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                <select onChange={(e) => setSelectedBrand(e.target.value)} value={selectedBrand}>
                    <option value="">Chọn Thương Hiệu</option>
                    {brands.map((brand) => (
                        <option key={brand._id} value={brand._id}>
                            {brand.name}
                        </option>
                    ))}
                </select>
                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                    <option value="">Chọn Loại</option>
                    {categories.map((category) => (
                        <option key={category._id} value={category._id}>
                            {category.name}
                        </option>
                    ))}
                </select>
                <input
                    type="number"
                    placeholder="Giá tối thiểu"
                    value={minPrice}
                    onChange={(e) => setMinPrice(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Giá tối đa"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(e.target.value)}
                />
                <button onClick={handleSearch}>Tìm kiếm</button>
            </div>

            {/* Hiển thị sản phẩm */}
            <div className="product-container">
                {products.map((product) => (
                    <div key={product._id} className="product-card">
                        <img
                            src={`http://localhost:5000${product.imageUrl[0]}`}
                            alt={product.name}
                        />
                        <h3>{product.name}</h3>
                        <p><strong>Đánh giá:</strong> {product.rating?.toFixed(1) || 0} ⭐</p>
                        <p><strong>Giá:</strong> {product.price.toLocaleString("vi-VN")} VNĐ</p>

                        <p><strong>Thương hiệu:</strong> {product.brand.name}</p>
                        <p><strong>Loại:</strong> {product.category.name}</p>
                        <button onClick={() => navigate(`/product/${product._id}`)}>Xem chi tiết</button>
                        <button style={{ marginTop: "5px" }} onClick={() => addToCart(product)}>🛒 Thêm vào giỏ hàng</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
