import axios from "axios";

const API_URL = "/api/reviews";

// Lấy tất cả đánh giá theo sản phẩm (hoặc bạn có thể tạo API lấy tất cả review nếu cần)
export const getReviewsByProduct = (productId) => {
    return axios.get(`${API_URL}/product/${productId}`);
};

// Gửi đánh giá mới (FE khách hàng dùng)
export const submitReview = (productId, data, token) => {
    return axios.post(`${API_URL}/${productId}`, data, {
        headers: { Authorization: `Bearer ${token}` }
    });
};

// Xóa đánh giá (admin)
export const deleteReview = (reviewId, token) => {
    return axios.delete(`${API_URL}/${reviewId}`, {
        headers: { Authorization: `Bearer ${token}` }
    });
};
export const reviewProduct = async (productId, reviewData) => {
    const token = localStorage.getItem("token");
    const res = await axios.post(`http://localhost:5000/api/reviews/${productId}`, reviewData, {
        headers: { Authorization: `Bearer ${token}` }
    });
    return res.data;
};
