import React, { useEffect, useState } from "react";
import { createOrderWithDetails } from "../../services/orderService";

const CartPage = () => {
  const userId = localStorage.getItem("userId");
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [message, setMessage] = useState("");

  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [note, setNote] = useState("");

  useEffect(() => {
    if (!userId) return;
    const cart = JSON.parse(localStorage.getItem(`cart_${userId}`)) || [];
    setCartItems(cart);
    setSelectedItems(cart.map(item => item._id));
  }, [userId]);

  const updateCart = (cart) => {
    setCartItems(cart);
    localStorage.setItem(`cart_${userId}`, JSON.stringify(cart));
  };

  const handleCheckout = async () => {
    if (!userId) return alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ ƒë·∫∑t h√†ng");
    if (!fullName || !email || !phone || !address) return setMessage("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin kh√°ch h√†ng");

    const itemsToOrder = cartItems.filter(item => selectedItems.includes(item._id));
    const orderData = {
      items: itemsToOrder.map(item => ({
        productId: item._id,
        quantity: item.quantity,
        price: item.price
      })),
      fullName, email, phone, address, note
    };

    try {
      await createOrderWithDetails(orderData);
      setMessage("‚úÖ ƒê·∫∑t h√†ng th√†nh c√¥ng!");
      const newCart = cartItems.filter(item => !selectedItems.includes(item._id));
      updateCart(newCart);
    } catch (err) {
      setMessage("‚ùå L·ªói khi ƒë·∫∑t h√†ng: " + (err.message || "Kh√¥ng x√°c ƒë·ªãnh"));
    }
  };

  return (
    <div>
      <h2>üõí Gi·ªè h√†ng</h2>
      {cartItems.map(item => (
        <div key={item._id}>
          {item.name} - {item.quantity} x {item.price} VND
        </div>
      ))}

      <h3>üìã Th√¥ng tin kh√°ch h√†ng</h3>
      <input placeholder="H·ªç t√™n" value={fullName} onChange={(e) => setFullName(e.target.value)} />
      <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input placeholder="SƒêT" value={phone} onChange={(e) => setPhone(e.target.value)} />
      <input placeholder="ƒê·ªãa ch·ªâ" value={address} onChange={(e) => setAddress(e.target.value)} />
      <textarea placeholder="Ghi ch√∫" value={note} onChange={(e) => setNote(e.target.value)}></textarea>
      <button onClick={handleCheckout}>Thanh to√°n</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default CartPage;
