import axios from "axios";

const API_URL = "http://localhost:5000/api/products";

export const getAuthHeaders = () => {
    const token = localStorage.getItem("token");
    if (!token) {
        console.error("‚ùå Kh√¥ng t√¨m th·∫•y token trong localStorage!");
        return null;
    }
    return {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
    };
};

// üü¢ L·∫•y danh s√°ch s·∫£n ph·∫©m
export const getProducts = async () => {
    try {
        const res = await axios.get(API_URL);
        return res.data;
    } catch (error) {
        console.error("‚ùå L·ªói khi l·∫•y danh s√°ch s·∫£n ph·∫©m:", error.response?.data || error.message);
        return [];
    }
};

// üü¢ Th√™m s·∫£n ph·∫©m m·ªõi
export const createProduct = async (formData) => {
    const headers = getAuthHeaders();
    if (!headers) return;

    try {
        const res = await axios.post(API_URL, formData, { headers });
        return res.data;
    } catch (error) {
        console.error("‚ùå L·ªói khi th√™m s·∫£n ph·∫©m:", error.response?.data || error.message);
        throw error;
    }
};

// üü¢ C·∫≠p nh·∫≠t s·∫£n ph·∫©m
export const updateProduct = async (id, formData) => {
    const headers = getAuthHeaders();
    if (!headers) return;

    try {
        const res = await axios.put(`${API_URL}/${id}`, formData, { headers });
        return res.data;
    } catch (error) {
        console.error(`‚ùå L·ªói khi c·∫≠p nh·∫≠t s·∫£n ph·∫©m ID ${id}:`, error.response?.data || error.message);
        throw error;
    }
};

