    const headers = getAuthHeaders();
    if (!headers) return;

    try {
        const res = await axios.delete(`${API_URL}/${id}`, { headers });
        return res.data;
    } catch (error) {
        console.error(`‚ùå L·ªói khi x√≥a s·∫£n ph·∫©m ID ${id}:`, error.response?.data || error.message);
        throw error;
