import React, { useEffect, useState } from "react";
import axios from "axios";

const OrderManagement = () => {
    const [orders, setOrders] = useState([]);
    const [message, setMessage] = useState("");

    // Lấy danh sách đơn hàng từ server
    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/orders/all", {
                    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
                });
                setOrders(response.data);
            } catch (error) {
                console.error("Error fetching orders", error);
                setMessage("Lỗi khi lấy đơn hàng");
            }
        };
        fetchOrders();
    }, []);

    // Cập nhật trạng thái đơn hàng
    const handleStatusChange = async (orderId, status) => {
        try {
            const response = await axios.put(
                "http://localhost:5000/api/orders/update-status",
                { orderId, status },
                {
                    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
                }
            );
            setMessage(response.data.message);
            setOrders(orders.map(order => (order._id === orderId ? { ...order, status } : order)));
        } catch (error) {
            setMessage("Lỗi khi cập nhật trạng thái đơn hàng");
        }
    };

    // Xóa đơn hàng
    const handleDeleteOrder = async (orderId) => {
        try {
            const response = await axios.delete(`http://localhost:5000/api/orders/${orderId}`, {
                headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
            });
            setMessage(response.data.message);
            setOrders(orders.filter(order => order._id !== orderId));
        } catch (error) {
            setMessage("Lỗi khi xóa đơn hàng");
        }
    };

    return (
        <div>
            <h2>Quản lý đơn hàng</h2>
            {message && <p>{message}</p>}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Người dùng</th>
                        <th>Tổng tiền</th>
                        <th>Trạng thái</th>
                        <th>Cập nhật trạng thái</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(order => (
                        <tr key={order._id}>
                            <td>{order._id}</td>
                            <td>{order.userId.username}</td>
                            <td>{order.totalPrice} VND</td>
                            <td>{order.status}</td>
                            <td>
                                <select
                                    value={order.status}
                                    onChange={(e) => handleStatusChange(order._id, e.target.value)}
                                >
                                    <option value="Chờ xác nhận">Chờ xác nhận</option>
                                    <option value="Đã giao">Đã giao</option>
                                    <option value="Đang xử lý">Đang xử lý</option>
                                    <option value="Đã hủy">Đã hủy</option>
                                </select>
                            </td>
                            <td>
                                <button onClick={() => handleDeleteOrder(order._id)}>Xóa</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default OrderManagement;
