            } catch (error) {
                console.error("L·ªói khi l·∫•y l·ªãch s·ª≠ ƒë∆°n h√†ng", error);
                setMessage("L·ªói khi l·∫•y l·ªãch s·ª≠ ƒë∆°n h√†ng");
            }
        };

        fetchOrders();
    }, []);

    return (
        <div className="order-history-container">
            <h2>L·ªãch s·ª≠ ƒë∆°n h√†ng c·ªßa b·∫°n</h2>
            {message && <p className="message">{message}</p>}
            {orders.length === 0 ? (
                <p>Kh√¥ng c√≥ ƒë∆°n h√†ng n√†o.</p>
            ) : (
                <table className="order-history-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Ng√†y t·∫°o</th>
                            <th>T·ªïng ti·ªÅn</th>
                            <th>Tr·∫°ng th√°i</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orders.map(order => (
                            <tr key={order._id}>
                                <td>{order._id}</td>
                                <td>{new Date(order.createdAt).toLocaleString()}</td>
                                <td>{order.totalPrice.toLocaleString()} VND</td>
                                <td className={`order-status ${order.status.toLowerCase()}`}>
                                    {order.status}
