import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getProducts, createProduct, deleteProduct } from "../../../services/productService";
import { getBrands } from "../../../services/brandService";
import { getCategories } from "../../../services/categoryService";

const ProductPage = () => {
    const [products, setProducts] = useState([]);
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [newProduct, setNewProduct] = useState({
        name: "", price: "", stock: "", description: "",
        size: "", colors: "", brand: "", category: "", imageUrl: null
    });

    const token = localStorage.getItem("token");
    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        setProducts(await getProducts());
        setBrands(await getBrands());
        setCategories(await getCategories());
    };

    const handleCreateProduct = async () => {
        if (!newProduct.name || !newProduct.price || !newProduct.stock || !newProduct.category || !newProduct.brand) {
            return alert("Vui lòng nhập đầy đủ thông tin sản phẩm!");
        }
        try {
            let formData = new FormData();
            formData.append("name", newProduct.name);
            formData.append("price", newProduct.price);
            formData.append("stock", newProduct.stock);
            formData.append("description", newProduct.description);
            formData.append("size", newProduct.size);
            formData.append("colors", newProduct.colors);
            formData.append("brand", newProduct.brand);
            formData.append("category", newProduct.category);
            if (newProduct.imageUrl) {
                formData.append("image", newProduct.imageUrl);
            }

            await createProduct(formData, token);
            setNewProduct({ name: "", price: "", stock: "", description: "", size: "", colors: "", brand: "", category: "", imageUrl: null });
            fetchData();
        } catch (error) {
            alert("Không thể thêm sản phẩm. Kiểm tra log!");
        }
    };

    const handleFileChange = (e) => {
        setNewProduct({ ...newProduct, imageUrl: e.target.files[0] });
    };

    return (
        <div>
            <h2>Manage Products</h2>

            {/* Form thêm sản phẩm */}
            <input placeholder="Product Name" value={newProduct.name} onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })} />
            <input placeholder="Price" type="number" value={newProduct.price} onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })} />
            <input placeholder="Stock" type="number" value={newProduct.stock} onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })} />
            <input placeholder="Description" value={newProduct.description} onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })} />
            <input placeholder="Size (comma-separated)" value={newProduct.size} onChange={(e) => setNewProduct({ ...newProduct, size: e.target.value.split(",") })} />
            <input placeholder="Colors (comma-separated)" value={newProduct.colors} onChange={(e) => setNewProduct({ ...newProduct, colors: e.target.value.split(",") })} />
            <input type="file" onChange={handleFileChange} />

            <select value={newProduct.category} onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}>
                <option value="">Select Category</option>
                {categories.map(category => <option key={category._id} value={category._id}>{category.name}</option>)}
            </select>

            <select value={newProduct.brand} onChange={(e) => setNewProduct({ ...newProduct, brand: e.target.value })}>
                <option value="">Select Brand</option>
                {brands.map(brand => <option key={brand._id} value={brand._id}>{brand.name}</option>)}
            </select>

            <button onClick={handleCreateProduct}>Add Product</button>

            {/* Bảng danh sách sản phẩm */}
            <table border="1">
                <thead>
                    <tr>
                        <th>Product Name</th>
                        <th>Price</th>
                        <th>Stock</th>
                        <th>Description</th>
                        <th>Size</th>
                        <th>Colors</th>
                        <th>Image</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product._id}>
                            <td>{product.name}</td>
                            <td>{product.price}</td>
                            <td>{product.stock}</td>
                            <td>{product.description}</td>
                            <td>{product.size.join(", ")}</td>
                            <td>{product.colors.join(", ")}</td>
                            <td>
                                {product.imageUrl && product.imageUrl.length > 0 ? (
                                    <img src={`http://localhost:5000${product.imageUrl[0]}`} alt={product.name} width="50" />
                                ) : (
                                    "No Image"
                                )}
                            </td>
                            <td>
                                <button onClick={() => navigate(`/edit-product/${product._id}`)}>Edit</button>
                                <button onClick={() => deleteProduct(product._id, token)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ProductPage;
