// frontend/pages/admin/Dashboard.jsx

import React from "react";
import { Link, useNavigate, Outlet } from "react-router-dom";
import "./css/Dashboard.css"; // üëà Import CSS t√πy ch·ªânh

const Dashboard = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("userId");
    localStorage.removeItem("username");
    navigate("/login");
  };

  return (
    <div className="admin-dashboard d-flex">
      <aside className="sidebar">
        <h2 className="sidebar-title">Admin Panel</h2>
        <ul className="sidebar-menu">
          <li><Link to="/admin/brands">üì¶ Brands</Link></li>
          <li><Link to="/admin/categories">üìÇ Categories</Link></li>
          <li><Link to="/admin/products">üõí Products</Link></li>
          <li><Link to="/admin/reviews">‚≠ê Reviews</Link></li>
          <li><Link to="/admin/orders">üìë Orders</Link></li>
        </ul>
        <button className="logout-btn" onClick={handleLogout}>üö™ Logout</button>
      </aside>
      <main className="dashboard-content">
  <Outlet /> {/* ‚úÖ Quan tr·ªçng ƒë·ªÉ render n·ªôi dung con */}
</main>
    </div>
  );
};

export default Dashboard;
