import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getProductById, getProductsByBrand, reviewProduct } from "../../services/productService";
import './ProductDetail.css';  // Import CSS cho trang chi tiết sản phẩm

const ProductDetail = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [similarProducts, setSimilarProducts] = useState([]);
    const [comment, setComment] = useState("");
    const [rating, setRating] = useState(5);

    useEffect(() => {
        fetchProduct();
    }, [id]);

    const fetchProduct = async () => {
        const data = await getProductById(id);
        setProduct(data);
        if (data && data.brand) {
            fetchSimilarProducts(data.brand._id); // Fetch sản phẩm tương tự cùng thương hiệu
        }
    };

    const fetchSimilarProducts = async (brandId) => {
        try {
            const response = await getProductsByBrand(brandId); // Lấy sản phẩm cùng thương hiệu
            setSimilarProducts(response.filter(p => p._id !== id)); // Lọc bỏ sản phẩm hiện tại
        } catch (error) {
            console.error("Error fetching similar products:", error);
        }
    };

    const handleReviewSubmit = async (e) => {
        e.preventDefault();
        try {
            await reviewProduct(id, { comment, rating });
            alert("✅ Đánh giá đã được gửi!");
            setComment("");
            setRating(5);
            fetchProduct(); // Cập nhật lại dữ liệu sản phẩm sau khi gửi đánh giá
        } catch (error) {
            alert(error.response?.data?.message || "❌ Lỗi khi gửi đánh giá");
        }
    };

    if (!product) {
        return <h2>Đang tải dữ liệu...</h2>;
    }

    return (
        <div className="product-detail-container">
            <h2>Chi tiết sản phẩm</h2>
            <div className="product-info">
                <div className="product-image">
                    <img src={`http://localhost:5000${product.imageUrl}`} alt={product.name} />
                </div>
                <div className="product-details">
                    <h3>{product.name}</h3>
                    <p><strong>Giá:</strong> {product.price} VND</p>
                    <p><strong>Mô tả:</strong> {product.description}</p>
                    <p><strong>Số lượng:</strong> {product.stock}</p>
                    <p><strong>Kích cỡ:</strong> {product.size.join(", ")}</p>
                    <p><strong>Màu sắc:</strong> {product.colors.join(", ")}</p>
                    <p><strong>Đánh giá trung bình:</strong> {product.rating.toFixed(1)} ⭐</p>
                </div>
            </div>

            <hr />

            <div className="review-form">
                <h3>Gửi đánh giá</h3>
                <form onSubmit={handleReviewSubmit}>
                    <label>Điểm đánh giá (1 - 5):</label>
                    <input
                        type="number"
                        min={1}
                        max={5}
                        value={rating}
                        onChange={(e) => setRating(Number(e.target.value))}
                        required
                    />
                    <br />
                    <label>Bình luận:</label>
                    <textarea
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        required
                    ></textarea>
                    <br />
                    <button type="submit">Gửi đánh giá</button>
                </form>
            </div>

            <hr />

            <div className="reviews-list">
                <h3>Đánh giá từ người dùng</h3>
                {product.reviews.length === 0 ? (
                    <p>Chưa có đánh giá nào.</p>
                ) : (
                    <ul>
                        {product.reviews.map((r, index) => (
                            <li key={index}>
                                <strong>{r.rating} ⭐</strong> - {r.comment} ({new Date(r.createdAt).toLocaleString()})
                            </li>
                        ))}
                    </ul>
                )}
            </div>

            <hr />

            <div className="similar-products">
                <h3>Sản phẩm tương tự</h3>
                {similarProducts.length === 0 ? (
                    <p>Không có sản phẩm tương tự.</p>
                ) : (
                    similarProducts.map((similarProduct) => (
                        <div key={similarProduct._id} className="product-card">
                            <img
                                src={`http://localhost:5000${similarProduct.imageUrl}`}
                                alt={similarProduct.name}
                            />
                            <h4>{similarProduct.name}</h4>
                            <p>{similarProduct.price} VND</p>
                            <button onClick={() => window.location.href = `/product/${similarProduct._id}`}>Xem chi tiết</button>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default ProductDetail;
