import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getBrands, getCategories } from "../../services/productService";
import './css/HomeStyles.css'; // CSS

const Home = () => {
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedBrand, setSelectedBrand] = useState("");
    const [selectedCategory, setSelectedCategory] = useState("");
    const [priceRange, setPriceRange] = useState("");

    const navigate = useNavigate();

    // L·∫•y brands v√† categories
    useEffect(() => {
        const fetchBrands = async () => {
            const data = await getBrands();
            setBrands(data);
        };

        const fetchCategories = async () => {
            const data = await getCategories();
            setCategories(data);
        };

        fetchBrands();
        fetchCategories();
    }, []);

    // Th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng
    const addToCart = (product) => {
        const token = localStorage.getItem("token");
        const userId = localStorage.getItem("userId");

        if (!token || !userId) {
            alert("‚ö† B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng.");
            navigate("/login");
            return;
        }

        const cartKey = `cart_${userId}`;
        const cart = JSON.parse(localStorage.getItem(cartKey)) || [];
        const existingIndex = cart.findIndex(item => item._id === product._id);

        if (existingIndex !== -1) {
            cart[existingIndex].quantity += 1;
        } else {
            cart.push({ ...product, quantity: 1 });
        }

        localStorage.setItem(cartKey, JSON.stringify(cart));
        alert("üõí S·∫£n ph·∫©m ƒë√£ ƒë∆∞·ª£c th√™m v√†o gi·ªè h√†ng!");
    };

    // X·ª≠ l√Ω l·ªçc v√† ƒëi·ªÅu h∆∞·ªõng khi nh·∫•n "T√¨m ki·∫øm"
    const handleSearch = () => {
        let min = "";
        let max = "";

        if (priceRange === "0-500000") {
            min = 0;
            max = 500000;
        } else if (priceRange === "500000-1000000") {
            min = 500000;
            max = 1000000;
        }

        const filters = {};

        if (searchQuery.trim() !== "") filters.name = searchQuery.trim();
        if (selectedBrand !== "") filters.brand = selectedBrand;
        if (selectedCategory !== "") filters.category = selectedCategory;
        if (min !== "" && max !== "") {
            filters.minPrice = min;
            filters.maxPrice = max;
        }

        // ƒêi·ªÅu h∆∞·ªõng sang trang k·∫øt qu·∫£ v·ªõi filters
        navigate("/search", { state: filters });
    };

    return (
        <div>
            <h2>Danh s√°ch s·∫£n ph·∫©m</h2>

            {/* T√¨m ki·∫øm v√† b·ªô l·ªçc */}
            <div className="search-container">
                <input
                    type="text"
                    placeholder="T√¨m ki·∫øm theo t√™n s·∫£n ph·∫©m..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                <select onChange={(e) => setSelectedBrand(e.target.value)} value={selectedBrand}>
                    <option value="">Ch·ªçn Th∆∞∆°ng Hi·ªáu</option>
                    {brands.map((brand) => (
                        <option key={brand._id} value={brand._id}>
                            {brand.name}
                        </option>
                    ))}
                </select>
                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                    <option value="">Ch·ªçn Lo·∫°i</option>
                    {categories.map((category) => (
                        <option key={category._id} value={category._id}>
                            {category.name}
                        </option>
                    ))}
                </select>
                <select onChange={(e) => setPriceRange(e.target.value)} value={priceRange}>
                    <option value="">Ch·ªçn kho·∫£ng gi√°</option>
                    <option value="0-500000">0 - 500.000 VNƒê</option>
                    <option value="500000-1000000">500.000 - 1.000.000 VNƒê</option>
                </select>
                <button onClick={handleSearch}>T√¨m ki·∫øm</button>
            </div>

            {/* C√≥ th·ªÉ hi·ªÉn th·ªã th√™m s·∫£n ph·∫©m n·ªïi b·∫≠t, ho·∫∑c s·∫£n ph·∫©m m·ªõi ·ªü ƒë√¢y */}
        </div>
    );
};

export default Home;
