import React, { useEffect, useState } from "react";
import CategoryList from "./CategoryList";
import CategoryForm from "./CategoryForm";
import { getCategories, deleteCategory } from "../../../services/categoryService";
import "../Brand/BrandStyles.css"; // Dùng chung file CSS với Brand để đồng bộ giao diện

const CategoryPage = () => {
    const [categories, setCategories] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [editCategory, setEditCategory] = useState(null);

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        setCategories(await getCategories());
    };

    const handleDelete = async (id) => {
        if (window.confirm("Bạn có chắc muốn xóa Category này?")) {
            await deleteCategory(id);
            fetchCategories();
        }
    };

    return (
        <div className="category-container">
            <h2>Manage Categories</h2>
            <button className="add-btn" onClick={() => setShowForm(true)}>➕ Add Category</button>

            <CategoryList categories={categories} onEdit={(category) => {
                setEditCategory(category);
                setShowForm(true);
            }} onDelete={handleDelete} />

            {showForm && <CategoryForm onClose={() => setShowForm(false)} onCategoryAdded={fetchCategories} editCategory={editCategory} />}
        </div>
    );
};

export default CategoryPage;
