import React, { useEffect, useState } from "react";
import axios from "axios";
import { updateOrderStatus, deleteOrder } from "../../../services/orderService";  // Import các service

const OrderManagement = () => {
    const [orders, setOrders] = useState([]);
    const [message, setMessage] = useState("");

    // Lấy danh sách đơn hàng khi component mount
    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/orders/all", {
                    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
                });
                setOrders(response.data);
            } catch (error) {
                console.error("Lỗi khi lấy đơn hàng", error);
                setMessage("Lỗi khi lấy đơn hàng");
            }
        };
        fetchOrders();
    }, []);

    // Cập nhật trạng thái đơn hàng
    const handleStatusChange = async (orderId, status) => {
        try {
            const response = await updateOrderStatus(orderId, status);  // Gọi service cập nhật trạng thái
            setMessage(response.message);
            setOrders(orders.map(order => (order._id === orderId ? { ...order, status } : order)));  // Cập nhật trạng thái
        } catch (error) {
            setMessage("Lỗi khi cập nhật trạng thái đơn hàng");
        }
    };

    // Xóa đơn hàng
    const handleDeleteOrder = async (orderId) => {
        try {
            const response = await deleteOrder(orderId);  // Gọi service xóa đơn hàng
            setMessage(response.message);
            setOrders(orders.filter(order => order._id !== orderId));  // Xóa đơn hàng khỏi danh sách
        } catch (error) {
            setMessage("Lỗi khi xóa đơn hàng");
        }
    };

    return (
        <div>
            <h2>Quản lý đơn hàng</h2>
            {message && <p>{message}</p>}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Người dùng</th>
                        <th>Tổng tiền</th>
                        <th>Trạng thái</th>
                        <th>Cập nhật trạng thái</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(order => (
                        <tr key={order._id}>
                            <td>{order._id}</td>
                            <td>{order.userId.username}</td>
                            <td>{order.totalPrice} VND</td>
                            <td>{order.status}</td>
                            <td>
                                <select
                                    value={order.status}
                                    onChange={(e) => handleStatusChange(order._id, e.target.value)} // Gọi handleStatusChange khi thay đổi
                                >
                                    <option value="Chờ xác nhận">Chờ xác nhận</option>
                                    <option value="Đang giao">Đã giao</option>
                                    <option value="Hoàn thành">Đang xử lý</option>
                                    <option value="Đã hủy">Đã hủy</option>
                                </select>
                            </td>
                            <td>
                                <button onClick={() => handleDeleteOrder(order._id)}>Xóa</button> {/* Gọi handleDeleteOrder khi nhấn Xóa */}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default OrderManagement;
