import React from "react";

const Footer = () => {
  return (
    <footer
      style={{
        backgroundColor: "#333",
        color: "white",
        padding: "20px 0", // Increase padding for a more spacious footer
        textAlign: "center",
        width: "100%",
        boxSizing: "border-box",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "center", // Center the content horizontally
          gap: "30px", // Space out the items
          marginBottom: "10px",
        }}
      >
        <p style={{ margin: 0 }}>© {new Date().getFullYear()} My E-Commerce Website</p>
        <p style={{ margin: 0 }}>
          Made with <span role="img" aria-label="love">❤️</span> by Your Name
        </p>
      </div>
      <div style={{ marginTop: "10px", fontSize: "14px" }}>
        <p style={{ margin: 0 }}>All rights reserved.</p>
      </div>
    </footer>
  );
};

export default Footer;
