import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from "react-router-dom";

// Import Pages
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import Home from "./components/pages/home/Home";
import ProductDetail from "./components/pages/home/ProductDetail";
import CartPage from "./components/pages/Cart/CartPage";
import OrderHistory from "./components/pages/home/OrderHistory";
import SearchResults from "./components/pages/home/SearchResults";

// Admin Layout and Pages
import Dashboard from "./components/pages/admin/Dashboard";
import BrandPage from "./components/pages/admin/Brand/BrandPage";
import CategoryPage from "./components/pages/admin/Category/CategoryPage";
import ProductPage from "./components/pages/admin/Product/ProductPage";
import ReviewPage from "./components/pages/admin/Review/ReviewPage";
import OrderPage from "./components/pages/admin/Order/OrderManagement";
import UserPage from "./components/pages/admin/User/UserPage";
import OrderDetailManagement from "./components/pages/admin/OrderDetail/OrderDetailManagement";

// Import Footer component
import Footer from "./components/Footer";  

// Navbar Component
const Navbar = ({ role, setRole }) => {
    const navigate = useNavigate();
    const [username, setUsername] = useState("");

    useEffect(() => {
        const storedUsername = localStorage.getItem("username");
        setUsername(storedUsername || "");
    }, [role]);

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        localStorage.removeItem("username");
        setRole("");
        navigate("/login");
    };

    return (
        <nav style={{ display: "flex", justifyContent: "space-between", padding: "10px", background: "#ddd", alignItems: "center" }}>
            <div>
                <button onClick={() => navigate("/")}>üè† Home</button>
                {role === "admin" && <button onClick={() => navigate("/admin")}>üîß Admin</button>}
                {role === "user" && <button onClick={() => navigate("/order-history")}>üìù L·ªãch s·ª≠ ƒë∆°n h√†ng</button>}
            </div>
            <div>
                <button onClick={() => navigate("/cart")}>üõí Gi·ªè h√†ng</button>
                {role ? (
                    <>
                        <span style={{ margin: "0 10px", fontWeight: "bold" }}>üë§ {username}</span>
                        <button onClick={handleLogout}>üö™ ƒêƒÉng Xu·∫•t</button>
                    </>
                ) : (
                    <>
                        <button onClick={() => navigate("/login")}>üîë ƒêƒÉng Nh·∫≠p</button>
                        <button onClick={() => navigate("/register")}>üìù ƒêƒÉng K√Ω</button>
                    </>
                )}
            </div>
        </nav>
    );
};

// AppRoutes Component
const AppRoutes = ({ role }) => {
    return (
        <Routes>
            {/* Public Routes */}
            <Route path="/" element={<Home />} />
            <Route path="/product/:id" element={<ProductDetail />} />
            <Route path="/cart" element={<CartPage />} />
            <Route path="/order-history" element={<OrderHistory />} />
            <Route path="/search-results" element={<SearchResults />} />

            {/* Auth Routes */}
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />

            {/* Admin Routes */}
            {role === "admin" ? (
                <Route path="/admin" element={<Dashboard />}>
                    <Route index element={<BrandPage />} />
                    <Route path="brands" element={<BrandPage />} />
                    <Route path="categories" element={<CategoryPage />} />
                    <Route path="products" element={<ProductPage />} />
                    <Route path="reviews" element={<ReviewPage />} />
                    <Route path="orders" element={<OrderPage />} />
                    <Route path="/admin/users" element={<UserPage />} />
                    <Route path="orderdetails" element={<OrderDetailManagement />} />
                </Route>
            ) : (
                <Route path="/admin/*" element={<Navigate to="/" />} />
            )}

            {/* Fallback Route */}
            <Route path="*" element={<Navigate to="/" />} />
        </Routes>
    );
};

// Main App Component
const App = () => {
    const [role, setRole] = useState(localStorage.getItem("role") || "");

    return (
        <Router>
            <Navbar role={role} setRole={setRole} />
            <div style={{ minHeight: "100vh", paddingBottom: "50px" }}>
                <AppRoutes role={role} />
            </div>
            <Footer />
        </Router>
    );
};

export default App;
