import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
    getBrands, createBrand, updateBrand, deleteBrand
} from "../services/brandService";
import {
    getCategories, createCategory, updateCategory, deleteCategory
} from "../services/categoryService";
import {
    getProducts, createProduct, updateProduct, deleteProduct
} from "../services/productService";

const Admin = () => {
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [newBrand, setNewBrand] = useState("");
    const [newCategory, setNewCategory] = useState("");
    const [newProduct, setNewProduct] = useState({
        name: "", price: "", category: "", brand: "",
        stock: "", description: "", size: "", colors: "", imageUrl: ""
    });
    const token = localStorage.getItem("token");
    const navigate = useNavigate();

    useEffect(() => {
        fetchBrands();
        fetchCategories();
        fetchProducts();
    }, []);

    const fetchBrands = async () => setBrands(await getBrands());
    const fetchCategories = async () => setCategories(await getCategories());
    const fetchProducts = async () => setProducts(await getProducts());

    const handleCreateBrand = async () => {
        if (!newBrand) return alert("Bạn cần nhập tên thương hiệu!");
        try {
            await createBrand({ name: newBrand }, token);
            setNewBrand("");
            fetchBrands();
        } catch (error) {
            alert("Không thể thêm Brand. Kiểm tra log!");
        }
    };

    const handleUpdateBrand = async (id, newName) => {
        if (!newName) return;
        try {
            await updateBrand(id, { name: newName }, token);
            fetchBrands();
        } catch (error) {
            alert("Không thể cập nhật Brand!");
        }
    };

    const handleCreateCategory = async () => {
        if (!newCategory) return alert("Bạn cần nhập tên danh mục!");
        try {
            await createCategory({ name: newCategory }, token);
            setNewCategory("");
            fetchCategories();
        } catch (error) {
            alert("Không thể thêm Category. Kiểm tra log!");
        }
    };

    const handleUpdateCategory = async (id, newName) => {
        if (!newName) return;
        try {
            await updateCategory(id, { name: newName }, token);
            fetchCategories();
        } catch (error) {
            alert("Không thể cập nhật Category!");
        }
    };

    const handleCreateProduct = async () => {
        if (!newProduct.name || !newProduct.price || !newProduct.stock || !newProduct.category || !newProduct.brand) {
            return alert("Vui lòng nhập đầy đủ thông tin sản phẩm!");
        }
        try {
            await createProduct(newProduct, token);
            setNewProduct({ name: "", price: "", category: "", brand: "", stock: "", description: "", size: "", colors: "", imageUrl: "" });
            fetchProducts();
        } catch (error) {
            alert("Không thể thêm Product. Kiểm tra log!");
        }
    };

    const handleUpdateProduct = async (id, updatedProduct) => {
        if (!updatedProduct) return;
        try {
            await updateProduct(id, updatedProduct, token);
            fetchProducts();
        } catch (error) {
            alert("Không thể cập nhật Product!");
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        navigate("/login");
    };

    return (
        <div>
            <h1>Admin Panel</h1>
            {token && <button onClick={handleLogout}>Logout</button>}

            <h2>Manage Brands</h2>
            <input placeholder="Brand Name" value={newBrand} onChange={(e) => setNewBrand(e.target.value)} />
            <button onClick={handleCreateBrand}>Add Brand</button>
            <ul>
                {brands.map((brand) => (
                    <li key={brand._id}>
                        <input
                            defaultValue={brand.name}
                            onBlur={(e) => handleUpdateBrand(brand._id, e.target.value)}
                        />
                        <button onClick={() => deleteBrand(brand._id, token)}>Delete</button>
                    </li>
                ))}
            </ul>

            <h2>Manage Categories</h2>
            <input placeholder="Category Name" value={newCategory} onChange={(e) => setNewCategory(e.target.value)} />
            <button onClick={handleCreateCategory}>Add Category</button>
            <ul>
                {categories.map((category) => (
                    <li key={category._id}>
                        <input
                            defaultValue={category.name}
                            onBlur={(e) => handleUpdateCategory(category._id, e.target.value)}
                        />
                        <button onClick={() => deleteCategory(category._id, token)}>Delete</button>
                    </li>
                ))}
            </ul>

            <h2>Manage Products</h2>
            <input placeholder="Product Name" value={newProduct.name} onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })} />
            <input placeholder="Price" type="number" value={newProduct.price} onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })} />
            <input placeholder="Stock" type="number" value={newProduct.stock} onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })} />
            <input placeholder="Description" value={newProduct.description} onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })} />
            <input placeholder="Size (comma-separated)" value={newProduct.size} onChange={(e) => setNewProduct({ ...newProduct, size: e.target.value.split(",") })} />
            <input placeholder="Colors (comma-separated)" value={newProduct.colors} onChange={(e) => setNewProduct({ ...newProduct, colors: e.target.value.split(",") })} />
            <input placeholder="Image URLs (comma-separated)" value={newProduct.imageUrl} onChange={(e) => setNewProduct({ ...newProduct, imageUrl: e.target.value.split(",") })} />

            <select value={newProduct.category} onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}>
                <option value="">Select Category</option>
                {categories.map(category => <option key={category._id} value={category._id}>{category.name}</option>)}
            </select>

            <select value={newProduct.brand} onChange={(e) => setNewProduct({ ...newProduct, brand: e.target.value })}>
                <option value="">Select Brand</option>
                {brands.map(brand => <option key={brand._id} value={brand._id}>{brand.name}</option>)}
            </select>

            <button onClick={handleCreateProduct}>Add Product</button>

            <ul>
                {products.map((product) => (
                    <li key={product._id}>
                        <input defaultValue={product.name} onBlur={(e) => handleUpdateProduct(product._id, { name: e.target.value })} />
                        <button onClick={() => deleteProduct(product._id, token)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Admin;
