import React, { useEffect, useState } from "react";
import ProductList from "./ProductList";
import ProductForm from "./ProductForm";
import { getProducts, deleteProduct } from "../../../services/productService";
import "../Brand/BrandStyles.css";

const ProductPage = () => {
    const [products, setProducts] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [editProduct, setEditProduct] = useState(null);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        setProducts(await getProducts());
    };

    const handleDelete = async (id) => {
        if (window.confirm("Bạn có chắc muốn xóa sản phẩm này?")) {
            await deleteProduct(id);
            fetchProducts();
        }
    };

    return (
        <div className="product-container">
            <h2>Manage Products</h2>
            <button className="add-btn" onClick={() => setShowForm(true)}>➕ Add Product</button>

            <ProductList products={products} onEdit={(product) => {
                setEditProduct(product);
                setShowForm(true);
            }} onDelete={handleDelete} />

            {showForm && <ProductForm onClose={() => setShowForm(false)} onProductAdded={fetchProducts} editProduct={editProduct} />}
        </div>
    );
};

export default ProductPage;
