import React, { useEffect, useState } from "react";
import { getProducts, getAllBrands, getAllCategories } from "../../../services/productService";  // Ensure these services exist for fetching brands and categories
import { useNavigate } from "react-router-dom";

const Home = () => {
    const [products, setProducts] = useState([]);
    const [searchQuery, setSearchQuery] = useState(""); // Tìm kiếm theo tên
    const [selectedBrand, setSelectedBrand] = useState(""); // Lọc theo thương hiệu
    const [selectedCategory, setSelectedCategory] = useState(""); // Lọc theo loại
    const [minPrice, setMinPrice] = useState(""); // Giá tối thiểu
    const [maxPrice, setMaxPrice] = useState(""); // Giá tối đa
    const [brands, setBrands] = useState([]); // Dữ liệu thương hiệu
    const [categories, setCategories] = useState([]); // Dữ liệu danh mục
    const navigate = useNavigate();

    useEffect(() => {
        const fetchBrandsAndCategories = async () => {
            const brandData = await getAllBrands();
            const categoryData = await getAllCategories();
            setBrands(brandData);
            setCategories(categoryData);
        };
        fetchBrandsAndCategories();
    }, []);

    useEffect(() => {
        const fetchProducts = async () => {
            const filters = {
                name: searchQuery,
                brand: selectedBrand,
                category: selectedCategory,
                minPrice: minPrice,
                maxPrice: maxPrice
            };
            const data = await getProducts(filters);
            setProducts(data);
        };

        fetchProducts();
    }, [searchQuery, selectedBrand, selectedCategory, minPrice, maxPrice]); // Khi có thay đổi thì gọi lại API để lấy dữ liệu mới

    const addToCart = (product) => {
        const cart = JSON.parse(localStorage.getItem("cart")) || [];
        const existing = cart.find(item => item._id === product._id);

        if (existing) {
            existing.quantity += 1;
        } else {
            cart.push({ ...product, quantity: 1 });
        }

        localStorage.setItem("cart", JSON.stringify(cart));
        alert("🛒 Sản phẩm đã được thêm vào giỏ hàng!");
    };

    const handleSearch = () => {
        // Trigger fetch products with the current filters
        const filters = {
            name: searchQuery,
            brand: selectedBrand,
            category: selectedCategory,
            minPrice: minPrice,
            maxPrice: maxPrice
        };
        getProducts(filters).then(data => setProducts(data));
    };

    return (
        <div>
            <h2>Danh sách sản phẩm</h2>

            {/* Tìm kiếm và bộ lọc */}
            <div>
                {/* Tìm kiếm theo tên sản phẩm */}
                <input
                    type="text"
                    placeholder="Tìm kiếm theo tên sản phẩm..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                <select onChange={(e) => setSelectedBrand(e.target.value)} value={selectedBrand}>
                    <option value="">Chọn Thương Hiệu</option>
                    {brands.map((brand) => (
                        <option key={brand._id} value={brand._id}>
                            {brand.name}
                        </option>
                    ))}
                </select>
                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                    <option value="">Chọn Loại</option>
                    {categories.map((category) => (
                        <option key={category._id} value={category._id}>
                            {category.name}
                        </option>
                    ))}
                </select>
                <input
                    type="number"
                    placeholder="Giá tối thiểu"
                    value={minPrice}
                    onChange={(e) => setMinPrice(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Giá tối đa"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(e.target.value)}
                />
                {/* Button to trigger the search */}
                <button onClick={handleSearch}>Tìm kiếm</button>
            </div>

            {/* Hiển thị các sản phẩm */}
            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
                {products.map((product) => (
                    <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", width: "220px" }}>
                        <img
                            src={`http://localhost:5000${product.imageUrl}`}
                            alt={product.name}
                            style={{ width: "200px", height: "200px", objectFit: "cover" }}
                        />
                        <h3>{product.name}</h3>
                        <p><strong>Đánh giá:</strong> {product.rating?.toFixed(1) || 0} ⭐</p>
                        <p><strong>Giá:</strong> {product.price} VND</p>
                        <button onClick={() => navigate(`/product/${product._id}`)}>Xem chi tiết</button>
                        <button style={{ marginTop: "5px" }} onClick={() => addToCart(product)}>🛒 Mua</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
