import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getProducts, getBrands, getCategories } from "../../../services/productService";

const Home = () => {
    const [products, setProducts] = useState([]);
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [searchQuery, setSearchQuery] = useState(""); // Tìm kiếm theo tên
    const [selectedBrand, setSelectedBrand] = useState(""); // Lọc theo thương hiệu
    const [selectedCategory, setSelectedCategory] = useState(""); // Lọc theo loại
    const [minPrice, setMinPrice] = useState(""); // Giá tối thiểu
    const [maxPrice, setMaxPrice] = useState(""); // Giá tối đa
    const navigate = useNavigate();

    // Fetch products based on filters
    useEffect(() => {
        const fetchProducts = async () => {
            const filters = {
                name: searchQuery, // Tìm kiếm theo tên
                brand: selectedBrand, // Thương hiệu
                category: selectedCategory, // Loại
                minPrice: minPrice, // Giá tối thiểu
                maxPrice: maxPrice // Giá tối đa
            };
            const data = await getProducts(filters);
            setProducts(data);
        };

        fetchProducts();
    }, [searchQuery, selectedBrand, selectedCategory, minPrice, maxPrice]); // Khi bộ lọc thay đổi, gọi lại API để lấy dữ liệu

    // Fetch brands and categories
    useEffect(() => {
        const fetchBrands = async () => {
            const data = await getBrands();
            setBrands(data);
        };
        const fetchCategories = async () => {
            const data = await getCategories();
            setCategories(data);
        };
        fetchBrands();
        fetchCategories();
    }, []);

    // Thêm sản phẩm vào giỏ hàng
    const addToCart = (product) => {
        const cart = JSON.parse(localStorage.getItem("cart")) || [];
        const existing = cart.find(item => item._id === product._id);

        if (existing) {
            existing.quantity += 1;
        } else {
            cart.push({ ...product, quantity: 1 });
        }

        localStorage.setItem("cart", JSON.stringify(cart));
        alert("🛒 Sản phẩm đã được thêm vào giỏ hàng!");
    };

    // Xử lý khi nhấn nút tìm kiếm
    const handleSearch = () => {
        const filters = {
            name: searchQuery,
            brand: selectedBrand,
            category: selectedCategory,
            minPrice: minPrice,
            maxPrice: maxPrice
        };
        getProducts(filters).then(data => setProducts(data));
    };

    return (
        <div>
            <h2>Danh sách sản phẩm</h2>

            {/* Tìm kiếm và bộ lọc */}
            <div>
                {/* Tìm kiếm theo tên sản phẩm */}
                <input
                    type="text"
                    placeholder="Tìm kiếm theo tên sản phẩm..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                
                {/* Bộ lọc theo thương hiệu */}
                <select onChange={(e) => setSelectedBrand(e.target.value)} value={selectedBrand}>
                    <option value="">Chọn Thương Hiệu</option>
                    {brands.map((brand) => (
                        <option key={brand._id} value={brand._id}>
                            {brand.name}
                        </option>
                    ))}
                </select>
                
                {/* Bộ lọc theo loại sản phẩm */}
                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                    <option value="">Chọn Loại</option>
                    {categories.map((category) => (
                        <option key={category._id} value={category._id}>
                            {category.name}
                        </option>
                    ))}
                </select>
                
                {/* Bộ lọc theo giá */}
                <input
                    type="number"
                    placeholder="Giá tối thiểu"
                    value={minPrice}
                    onChange={(e) => setMinPrice(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Giá tối đa"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(e.target.value)}
                />

                {/* Nút tìm kiếm */}
                <button onClick={handleSearch}>Tìm kiếm</button>
            </div>

            {/* Hiển thị sản phẩm */}
            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
                {products.map((product) => (
                    <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", width: "220px" }}>
                        <img
                            src={`http://localhost:5000${product.imageUrl}`}
                            alt={product.name}
                            style={{ width: "200px", height: "200px", objectFit: "cover" }}
                        />
                        <h3>{product.name}</h3>
                        <p><strong>Đánh giá:</strong> {product.rating?.toFixed(1) || 0} ⭐</p>
                        <p><strong>Giá:</strong> {product.price} VND</p>
                        <p><strong>Thương hiệu:</strong> {product.brand.name}</p> {/* Hiển thị tên thương hiệu */}
                        <p><strong>Loại:</strong> {product.category.name}</p> {/* Hiển thị tên loại sản phẩm */}
                        <button onClick={() => navigate(`/product/${product._id}`)}>Xem chi tiết</button>
                        <button style={{ marginTop: "5px" }} onClick={() => addToCart(product)}>🛒 Mua</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
