import axios from "axios";

const API_URL = "http://localhost:5000/api/products";

// Lấy danh sách tất cả sản phẩm
export const getProducts = async () => {
    try {
        const res = await axios.get(API_URL);
        return res.data;
    } catch (error) {
        console.error("Lỗi khi lấy danh sách sản phẩm:", error.response?.data || error.message);
        return null; // Tránh crash ứng dụng khi API lỗi
    }
};

// Lấy thông tin chi tiết một sản phẩm theo ID
export const getProductById = async (id) => {
    try {
        const res = await axios.get(`${API_URL}/${id}`);
        return res.data;
    } catch (error) {
        console.error(`Lỗi khi lấy sản phẩm ID ${id}:`, error.response?.data || error.message);
        return null; // Tránh lỗi khi không tìm thấy sản phẩm
    }
};

// Hàm lấy token từ localStorage (tránh lặp lại code)
const getAuthHeaders = () => {
    const token = localStorage.getItem("token");
    if (!token) {
        console.error("⚠ Không tìm thấy token trong localStorage!");
        return null;
    }
    return {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
    };
};

// Thêm sản phẩm mới
export const createProduct = async (formData) => {
    const headers = getAuthHeaders();
    if (!headers) return;

    try {
        const res = await axios.post(API_URL, formData, { headers });
        console.log("API Response:", res.data);
        return res.data;
    } catch (error) {
        console.error("Lỗi khi thêm sản phẩm:", error.response?.data || error.message);
        throw error;
    }
};

// Cập nhật sản phẩm
export const updateProduct = async (id, formData) => {
    const headers = getAuthHeaders();
    if (!headers) return;

    try {
        const res = await axios.put(`${API_URL}/${id}`, formData, { headers });
        return res.data;
    } catch (error) {
        console.error(`Lỗi khi cập nhật sản phẩm ID ${id}:`, error.response?.data || error.message);
        throw error;
    }
};

// Xóa sản phẩm
export const deleteProduct = async (id) => {
    const headers = getAuthHeaders();
    if (!headers) return;

    try {
        const res = await axios.delete(`${API_URL}/${id}`, { headers });
        return res.data;
    } catch (error) {
        console.error(`Lỗi khi xóa sản phẩm ID ${id}:`, error.response?.data || error.message);
        throw error;
    }
};
