import React, { useEffect, useState } from "react";
import { deleteOrder } from "../../services/orderService";  // Import dịch vụ xóa đơn hàng

const OrderManagement = () => {
    const [orders, setOrders] = useState([]);
    const [message, setMessage] = useState("");

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/orders/all", {
                    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
                });
                setOrders(response.data);
            } catch (error) {
                console.error("Lỗi khi lấy đơn hàng", error);
                setMessage("Lỗi khi lấy đơn hàng");
            }
        };
        fetchOrders();
    }, []);

    const handleDeleteOrder = async (orderId) => {
        try {
            const response = await deleteOrder(orderId); // Gọi dịch vụ xóa đơn hàng
            setMessage(response.message);
            setOrders(orders.filter(order => order._id !== orderId)); // Cập nhật danh sách sau khi xóa
        } catch (error) {
            setMessage("Lỗi khi xóa đơn hàng");
        }
    };

    return (
        <div>
            <h2>Quản lý đơn hàng</h2>
            {message && <p>{message}</p>}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Người dùng</th>
                        <th>Tổng tiền</th>
                        <th>Trạng thái</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(order => (
                        <tr key={order._id}>
                            <td>{order._id}</td>
                            <td>{order.userId.username}</td>
                            <td>{order.totalPrice} VND</td>
                            <td>{order.status}</td>
                            <td>
                                <button onClick={() => handleDeleteOrder(order._id)}>Xóa</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default OrderManagement;
