import React from "react";
import { useNavigate } from "react-router-dom";

const Navbar = ({ role, setRole }) => {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        setRole("");
        navigate("/login");
    };

    return (
        <nav style={{ display: "flex", justifyContent: "space-between", padding: "10px", background: "#ddd", alignItems: "center" }}>
            <div>
                <button onClick={() => navigate("/")}>üè† Home</button>
                {role === "admin" && <button onClick={() => navigate("/admin")}>üîß Admin</button>}
                {role === "user" && <button onClick={() => navigate("/order-history")}>üìù L·ªãch s·ª≠ ƒë∆°n h√†ng</button>}
            </div>
            <div>
                <button onClick={() => navigate("/cart")}>üõí Gi·ªè h√†ng</button>
                {role ? (
                    <button onClick={handleLogout}>üö™ Logout</button>
                ) : (
                    <>
                        <button onClick={() => navigate("/login")}>üîë Login</button>
                        <button onClick={() => navigate("/register")}>üìù Register</button>
                    </>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
