import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import Home from "./components/pages/Home";
import Admin from "./components/pages/Admin";
import Logout from "./components/auth/Logout";

const App = () => {
  const [role, setRole] = useState(localStorage.getItem("role") || "");

  useEffect(() => {
    const handleStorageChange = () => setRole(localStorage.getItem("role") || "");
    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  return (
    <Router>
      {/* Hiển thị Logout khi đã đăng nhập   <Logout setRole={setRole} />*/}
      <Routes>
        <Route path="/login" element={<Login setRole={setRole} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/" element={role === "admin" ? <Navigate to="/admin" /> : <Home />} />
        <Route path="/admin" element={role === "admin" ? <Admin /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
