// ✅ frontend/src/components/pages/home/Home.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getProducts, getBrands, getCategories } from "../../services/productService";
import './css/HomeStyles.css';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [brands, setBrands] = useState([]);
  const [categories, setCategories] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedBrand, setSelectedBrand] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    const fetchProducts = async () => {
      const filters = { name: searchQuery, brand: selectedBrand, category: selectedCategory, minPrice, maxPrice };
      const data = await getProducts(filters);
      setProducts(data);
    };
    fetchProducts();
  }, [searchQuery, selectedBrand, selectedCategory, minPrice, maxPrice]);

  useEffect(() => {
    getBrands().then(setBrands);
    getCategories().then(setCategories);
  }, []);

  const addToCart = (product) => {
    if (!userId) {
      alert("⚠ Bạn cần đăng nhập để thêm vào giỏ hàng.");
      navigate("/login");
      return;
    }

    const cartKey = `cart_${userId}`;
    const cart = JSON.parse(localStorage.getItem(cartKey)) || [];
    const existing = cart.find(item => item._id === product._id);

    if (existing) {
      existing.quantity += 1;
    } else {
      cart.push({ ...product, quantity: 1 });
    }

    localStorage.setItem(cartKey, JSON.stringify(cart));
    alert("🛒 Sản phẩm đã được thêm vào giỏ hàng!");
  };

  return (
    <div>
      <h2>Danh sách sản phẩm</h2>
      {/* Tìm kiếm */}
      <div className="search-container">
        <input value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} placeholder="Tìm kiếm..." />
        <select value={selectedBrand} onChange={(e) => setSelectedBrand(e.target.value)}>
          <option value="">Thương hiệu</option>
          {brands.map(b => <option key={b._id} value={b._id}>{b.name}</option>)}
        </select>
        <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="">Loại</option>
          {categories.map(c => <option key={c._id} value={c._id}>{c.name}</option>)}
        </select>
        <input type="number" value={minPrice} onChange={(e) => setMinPrice(e.target.value)} placeholder="Giá min" />
        <input type="number" value={maxPrice} onChange={(e) => setMaxPrice(e.target.value)} placeholder="Giá max" />
        <button onClick={() => getProducts({ name: searchQuery, brand: selectedBrand, category: selectedCategory, minPrice, maxPrice }).then(setProducts)}>Tìm</button>
      </div>
      <div className="product-container">
        {products.map(product => (
          <div key={product._id} className="product-card">
            <img src={`http://localhost:5000${product.imageUrl[0]}`} alt={product.name} />
            <h3>{product.name}</h3>
            <p>Giá: {product.price} VND</p>
            <button onClick={() => addToCart(product)}>🛒 Thêm vào giỏ hàng</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
